@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(dota2.ru)
{
     .bn-btm, .abnpl24tp,
    .global-wrapper, .rock,
    div.subline ~ *, .a-in-after,
    html > body#id > div:not(:empty) > div:not([id="glob-wrap"]),
    [href*="mountain"], 
    [href*="plain"],
    [href*="leave"],
    section.m-memes + style + div,
    div.main-b-right + style + div + div,
    div.main-b-right-c-right > style + div,
    div.b-list > div + style + style + div
    {
     display: none !important;   
    }  
    
    .popup
    {
    display: inline-block !important; 
    }

    #glob-wrap
    {
    filter: none !important;
    -webkit-filter: none !important;
    }

    [class^="f1u5l5l4"], [class^="rf31u5l"], #agg2ada,
    html > body#id > div:not(:empty)
    {
     background-image: none !important;
     padding-top: 0 !important;
    }
    
}
