css:
@-moz-document domain("blablaland.com") {



body {
	background-color:#000000;/*#65C3FF;*/
	background:url(http://www.blablaland.com/site/images/_template/bg_halloween.png) center top;
}
	
	
#DECO {
	/*background-image: url(/web/20141104015409/http://www.blablaland.com/site/images/_template/bg_halloween_deco.png);*/
	background-image: url(http://www.blablaland.com/site/images/_template/fond_site_halloween.jpg);
}


#N99_BAR a {
	color:#ff8f00 ;
}

#N99_BAR a:hover, #N99_BAR a.N99_BAR_ACTIVE {
	background:#ff8f00;
}

#HEADER_INFOS_JEU {
	color:#ff8f00;
}

#MENU {
	background-color: #ef4300;
}

#FOOTER_CUT {
	background-color:#ef4300;
}

#MENU li a:hover span, #MENU li:hover a span, #MENU li a:focus span, #MENU li:focus a span, #MENU li a.menu_selected span {
	color:#ef4300;
	background-color:#FFFFFF;
	text-shadow: none;
}

#MENU li.menu_btn_jouer {
	background-color:#FFAD00;/*#444444;*/
}

#MENU_BTN_CTA a {
	background-color:#FFAD00;
}

#PROFIL_LOGIN_BOX .login_box_ok {
	background-color:#ef4300;
	}
	#PROFIL_LOGIN_BOX .login_box_ok:hover, #PROFIL_LOGIN_BOX .login_box_ok:focus {
		background-color: #FF733E;
	}


#MENU ul li ul li a:hover { background-color:#ef4300; }
#MENU ul ul a {
	border-top: solid 1px #FFAD00;
}


a.a_rounded {
	color:#ef4300;
	}
	a.a_rounded:hover {
		color:#FFAD00;
	}


#HEADER_LOGO_BLABLALAND {
    background-image: url("http://i.imgur.com/bBXls7w.png");
}





