@-moz-document url-prefix("https://www.nyaa.se/?page=f"),
url-prefix("https://www.nyaa.se/?page=l"),
url-prefix("https://www.nyaa.se/?page=m"),
url-prefix("https://www.nyaa.se/?page=p"),
url-prefix("https://www.nyaa.se/?page=sea"),
url-prefix("https://www.nyaa.se/?page=si"),
url-prefix("https://www.nyaa.se/?page=tor"),
url-prefix("https://www.nyaa.se/?page=u"),
url-prefix("https://www.nyaa.se/?page=v"),
url-prefix("https://www.nyaa.se/?c"),
url-prefix("https://www.nyaa.se/?u"),
url-prefix("https://sukebei.nyaa.se/?page=l"),
url-prefix("https://sukebei.nyaa.se/?page=m"),
url-prefix("https://sukebei.nyaa.se/?page=p"),
url-prefix("https://sukebei.nyaa.se/?page=sea"),
