url-prefix("https://sukebei.nyaa.se/?page=v"),
url-prefix("https://sukebei.nyaa.se/?c"),
url-prefix("https://sukebei.nyaa.se/?u"),
url("https://www.nyaa.se/"),
url("https://sukebei.nyaa.se/"){
body {
    min-width: 1000px !important;
    font-family: Segoe UI !important;
    background: url(http://colourlovers.com.s3.amazonaws.com/images/patterns/1024/1024167.png) fixed #eaeaea !important; }

a, a:hover {color: #3c3c3c !important;}
a img:hover {opacity: 0.8 !important;}

input, select, textarea {font-family: Segoe UI !important; font-size: 0.9em !important;}

#main {padding-top: 70px !important; min-width: 1000px !important;}

#searchcontainer {
    padding: 3px 0 5px !important; /* Chrome */
    float: none !important;
    width: 100% !important;
    font-size: 0.8em !important;
    text-align: center !important; }

.inputsearchterm {text-align: center !important;}

#navbar + form {
    margin: 0 !important;
    box-shadow: 0px 2px 4px #a5a5a5 !important;
    position: relative !important;
    top: -27px !important;
    background: white; }

#navbar {
    width: 40% !important;
    left: 30% !important;
    float: none !important;
    top: 29px !important;
    position: relative !important;
    text-align: center !important; }
#tabnav {
    float: none !important;
    height: 27px !important;
    background: #eaeaea !important;
    box-shadow: 0px 0px 8px #a5a5a5 !important;
    transition: 0.6s;
    opacity: 0 !important;
    margin: 0 !important;
    border-radius: 0 0 8px 8px; }
#tabnav:hover {opacity: 1 !important; transition: 0.2s;}

#tabnav a {
    float: none !important;
    color: #3c3c3c !important;
    padding: 3px 6px 4px 9px !important;
    font-size: 0.8em !important;
    position: relative !important;
    letter-spacing: 3px;
    top: 4px !important; }
#tabnav a:hover {
    text-align: center;
    background: white;
    border-radius: 3px;
    text-decoration: none; }

#topbar {
    background: transparent !important;
    z-index: 100;
    opacity: 0.95;
    height: 0 !important;
    xposition: absolute !important; /* Static Top Bar (Remove 'X') */ }

.content {
    background: white !important;
    z-index: -2; position: relative;
    margin-top: 70px !important;
    padding-bottom: 0 !important;
    width: 100% !important;
    border: none !important;
    border-radius: 8px 8px 0 0; }
.container {padding: 30px !important;}
.notice { 
    background: #323232 !important;
    display: inline-block !important;
    padding: 7px 11px 16px 24px !important;
    top: 39px !important;
    height: 11px;
    z-index: -1;
    position: relative !important;
    color: transparent !important;
    width: 96.5% !important; }
.notice a {text-decoration: none !important;}
.notice span {color: transparent !important;}

.tlist {width: 96% !important; top: -60px !important; position: relative;} /* Chrome */
.tlisticon {padding: 5px !important;}
.tlistthone {padding-left: 12px !important;}
.tlisthead {font-size: 0 !important; visibility: hidden;}
.tlistrow {border-top: 1px solid white !important; border-bottom: none !important;}
.tlistsize {border: none !important;}
.tlistsortorder { 
    background: #181818 !important;
    border-radius: 8px 8px 0 0;
    color: #686868 !important;
    padding: 11px 20px 12px !important;
    position: relative !important;
    top: -58px !important;
    width: 100% !important; }
.tlistsortorder td:nth-child(2) {color: transparent !important;}
.tlistsortorder td:nth-child(3) {text-align: right !important;}
.tlistsortorder a {color: #555555 !important; font-weight: bold !important;}
.tlistsortorder a:hover {color: #686868 !important; text-decoration: none !important;}

th {font-weight: 400 !important; padding-top: 7px !important;}

#main div[style*="float:"] {display: none !important;} /* Ads */
div[class*="chatbox"] {display: none !important;} /* Chat */

.leftpages, .pages, .rightpages {
    color: #606160 !important;
    background: #323232 !important;
    font-weight: bold !important;
    opacity: 0; transition: 0.6s;
    margin-bottom: 10px !important;
    top: -62px !important;
    position: relative !important;
    padding: 8px 0 9px !important; }
.leftpages:hover, .pages:hover, .rightpages:hover {opacity: 1; transition: 0.3s;}
.leftpages, .rightpages {font-size: 0.8em; padding: 10px 0 12px !important;}
.leftpages a, .pages a, .rightpages a {color: #606160 !important;}
.tlist + .leftpages, .tlist + .leftpages + .pages,
.tlist + .leftpages + .pages + .rightpages {
    position: relative !important;
    top: 10px !important;
    opacity: 1 !important;
    color: #aeaeae !important;
    background: #f4f4f4 !important; }
.tlist + .leftpages a, .tlist + .leftpages + .pages a,
.tlist + .leftpages + .pages + .rightpages a
{color: #aeaeae !important;}
.tlist + .leftpages a:hover, .tlist + .leftpages + .pages a:hover,
.tlist + .leftpages + .pages + .rightpages a:hover,
.tlist + .leftpages + .pages > b
{background: white !important;}
.page, .pages b {border: none !important; padding: 4px 5px 5px !important;}
.pagelink:hover, .pages b {background: #181818 !important; border-radius: 4px;}
.pagelink:hover {padding: 4px 5px 5px !important;}

.leftpages, .rightpages {width: 8% !important;}
.pages {width: 84% !important;}

.tlistmn {color: black !important;}

/* Input boxes */
.commentform, .inputflagreason, .inputptext, .inputultext,
.inputultextarea, input[name="torrent"] {
    width: 98.8% !important;
    background: white !important;
    border: 1px solid #e0e0e0;
    border-radius: 3px !important;
    padding: 3px 5px 5px !important;
    box-shadow: inset 0 1px 2px rgba(65,65,65,0.1);
    font-family: "Segoe UI" !important;
    font-size: 13px !important; }
.commentformdiv {text-align: left !important;}
.commentform + br + input[type="submit"] {margin-top: 5px !important;}
.commentform:focus, .inputflagreason:focus, .inputptext:focus,
.inputultext:focus, .inputultextarea:focus, input[name="torrent"]:focus {
    border-color: rgba(107,191,227,0.7) !important;
    box-shadow: 0 0 4px rgba(107,191,227,0.5) !important; }
.inputflagreason {width: 100.4% !important;}
.inputptext {width: 500px !important;}
input[name="torrent"] {width: 101.3% !important;}

/* Upload page */
.inputulrules {
    background: #6bc8ae;
    color: white;
    width: 476px;
    display: block;
    padding: 2px 4px 4px;
    border-radius: 3px;
    text-align: center;
    margin: 8px 0 6px 5px;
    font-size: 15px !important; }

.ulformtable tr:nth-child(1) .inputulname + td + td,
.ulformtable tr:nth-child(6) .inputulname + td + td,
.ulformtable tr:nth-child(10) .inputulname + td + td { 
    background: #f6f6f6 !important;
    border: 1px solid #d3d3d3 !important;
    border-radius: 4px 4px 15px;
    display: block;
    margin-left: 20px;
    padding: 10px;
    width: 90%; }

.ulformtable .inputulname {
    display: block !important;
    margin: 7px 0 !important;
    vertical-align: baseline !important;}

.ulformtable tr:nth-child(10) .inputulname {vertical-align: top !important;}

.inputulselect {width: 487px !important;}

/* Torrent page */
.content .viewdescription, .content .quote, .content .comment,
.content .viewfile, .content .spoiler {
    border: none !important;
    background: white !important;
    border-radius: 4px 4px 15px !important; }

.content .quote, .content .spoiler {
    background: none !important;
    border: none !important;
    padding: 10px !important;
    margin: 5px 10px !important;
    border-radius: 11px !important;
    border-bottom: 2px solid #6bbfe3 !important;
    border-left: 2px solid #6bbfe3 !important; }

div[class="content"] .viewdescription, div[class="content"] .quote,
div[class="content"] .comment, div[class="content"] .viewfile
{background: #f6f6f6 !important; border: 1px solid #d3d3d3 !important;}

.content .viewdescription {padding: 14px !important;}
.content .comment {padding: 6px !important; margin-bottom: 12px !important;}
.content .viewfile {padding: 6px 6px 10px !important;}
.content .viewdescription img {max-width: 908px !important;}

/* Comment number */
.cnumber {
    background: #eaeaea;
    box-shadow: 0 2px 4px #a5a5a5;
    border-radius: 4px 4px 4px 15px;
    font-size: 0.8em !important;
    text-align: center !important;
    left: 12px;
    padding: 4px 0 5px 2px;
    position: relative;
    top: -12px; }

/* Stardom */
div[class="content trusted"] .viewtable tr:nth-child(6) .tname:first-of-type,
div[class="content remake"] .viewtable tr:nth-child(6) .tname:first-of-type,
div[class="content hidden"] .viewtable tr:nth-child(6) .tname:first-of-type,
div[class="content aplus"] .viewtable tr:nth-child(6) .tname:first-of-type,
div[class="content"] .viewtable tr:nth-child(6) .tname:first-of-type {
    background: white;
    padding: 5px 5px 6px 41px !important;
    position: relative; top: 5px; left: -32px; }
div[class="content"] .viewtable tr:nth-child(6) .tname:first-of-type {
    color: white !important;
    background: #6bc8ae !important; }

div[class="content trusted"] .viewtable tr:nth-child(6) td:nth-child(2),
div[class="content remake"] .viewtable tr:nth-child(6) td:nth-child(2),
div[class="content hidden"] .viewtable tr:nth-child(6) td:nth-child(2),
div[class="content aplus"] .viewtable tr:nth-child(6) td:nth-child(2),
div[class="content"] .viewtable tr:nth-child(6) td:nth-child(2) {
    background: white;
    border-radius: 0 3px 15px 0;
    padding: 5px 5px 6px 33px !important;
    position: relative; top: 5px; left: -32px;
    display: block !important; width: 35%; }
div[class="content"] .viewtable tr:nth-child(6) td:nth-child(2)    {
    color: white !important;
    background: #6bc8ae !important; }

a[href*="?page=idolmaster"] {
    font-size: 0.8em !important;
    padding: 3px 4px !important;
    position: relative !important;
    top: -1px !important; }
div[class="content"] a[href*="?page=idolmaster"] {color: white !important;}

/* 'Show Files' file name length */
.fileentryname {width: 99.3% !important; padding-left: 5px !important;}

/* 'Show Files' dotted lines */
.fileentry {border: none !important;}

/* 'Status Unknown' */
.tlistfailed {
    text-decoration: underline;
    text-align: left !important;
    font-size: 0.8em !important; }

tr[class="trusted tlistrow"] .tlistmn, tr[class="remake tlistrow"] .tlistmn,
tr[class="hidden tlistrow"] .tlistmn, tr[class="aplus tlistrow"] .tlistmn,
tr[class="tlistrow"] .tlistmn {
    border-radius: 0 3px 15px 0 !important;
    display: block !important;
    padding: 15px 0 !important; }

tr[class="trusted tlistrow"] .tlistname, tr[class="trusted tlistrow"] .tlistdownload,
tr[class="trusted tlistrow"] .tlistsize, tr[class="trusted tlistrow"] .tlistsn,
tr[class="trusted tlistrow"] .tlistln, tr[class="trusted tlistrow"] .tlistdn,
tr[class="trusted tlistrow"] .tlisticon, tr[class="trusted tlistrow"] .tlistmn,
tr[class="trusted tlistrow"] .tlistfailed
{background: #98d9a8 !important;}

tr[class="remake tlistrow"] .tlistname, tr[class="remake tlistrow"] .tlistdownload,
tr[class="remake tlistrow"] .tlistsize, tr[class="remake tlistrow"] .tlistsn,
tr[class="remake tlistrow"] .tlistln, tr[class="remake tlistrow"] .tlistdn,
tr[class="remake tlistrow"] .tlisticon, tr[class="remake tlistrow"] .tlistmn,
tr[class="remake tlistrow"] .tlistfailed
{background: #f0b080 !important;}

tr[class="hidden tlistrow"] .tlistname, tr[class="hidden tlistrow"] .tlistdownload,
tr[class="hidden tlistrow"] .tlistsize, tr[class="hidden tlistrow"] .tlistsn,
tr[class="hidden tlistrow"] .tlistln, tr[class="hidden tlistrow"] .tlistdn,
tr[class="hidden tlistrow"] .tlisticon, tr[class="hidden tlistrow"] .tlistmn,
tr[class="hidden tlistrow"] .tlistfailed
{background: #c0c0c0 !important;}

tr[class="aplus tlistrow"] .tlistname, tr[class="aplus tlistrow"] .tlistdownload,
tr[class="aplus tlistrow"] .tlistsize, tr[class="aplus tlistrow"] .tlistsn,
tr[class="aplus tlistrow"] .tlistln, tr[class="aplus tlistrow"] .tlistdn,
tr[class="aplus tlistrow"] .tlisticon, tr[class="aplus tlistrow"] .tlistmn,
tr[class="aplus tlistrow"] .tlistfailed
{background: #60b0f0 !important;}

.remake {background: transparent !important;}
.trusted {background: transparent !important;}
.hidden {background: transparent !important;}
.aplus {background: transparent !important;}

div[class="content remake"], div[class="content trusted"],
div[class="content hidden"], div[class="content aplus"]
{border: 2px solid white !important; border-radius: 8px 8px 18px 18px !important;}

.refinesearch {
    background: white !important;
    display: block !important;
    border-radius: 8px 0 8px 8px;
    margin-left: -69px !important;
    margin-top: 73px !important;
    opacity: 0; transition: 0.4s;
    padding: 23px 9px 0 9px !important;
    position: absolute !important;
    font-size: 1em !important;
    text-align: center !important;
    width: 7.1% !important;
    z-index: 1; }
.refinesearch:hover {opacity: 1; transition: 0.2s;}
.refinesearch b, .refinesearch input[value="Search"] {display: none !important;}
.refinesearch input {
    width: 47px !important;
    text-align: center !important;
    background: white !important;
    border: 1px solid #e0e0e0 !important;
    border-radius: 3px !important;
    padding: 3px 5px 5px !important;
    box-shadow: inset 0 1px 2px rgba(65,65,65,0.1);
    font-family: "Segoe UI" !important; }
.refinesearch input:focus {
    border-color: rgba(107,191,227,0.7) !important;
    box-shadow: 0 0 4px rgba(107,191,227,0.5) !important; }

img[alt="DL"] {
    background: transparent !important;
    border: 2px dashed #3c3c3c !important;
    border-radius: 50%;
    display: block;
    width: 0 !important;
    margin: 4px !important;
    padding: 3px !important; }

.torrentcats {
    position: absolute !important;
    background: #323232 !important;
    display: inline-block !important;
    top: -10px !important;
    z-index: -1;
    height: 34px !important;
    width: 100% !important;
    color: transparent !important; }
.torrentcats a {visibility: hidden;}

.viewtable + div[style="float:left;padding-left:20px;"] {padding: 15px 0 0 0 !important;}

.viewdownloadbutton {width: 4% !important; padding-top: 0 !important; padding-right: 3px !important;}
.viewdownloadtxtbutton {width: 26% !important; padding-top: 0 !important;}

img[alt="Download"], img[alt="Download .txt"] { 
    background: transparent !important;
    border: 3px inset green !important;
    border-radius: 50%;
    display: block;
    width: 0 !important;
    margin: 4px !important;
    padding: 8px !important; }
img[alt="Download .txt"] {border: 3px outset green !important;}
img[alt="Download"]:hover {border: 3px outset green !important;}
img[alt="Download .txt"]:hover {border: 3px inset green !important;}

form[name="flagform"] {margin: 40px 0 0 -20px !important;}
input[value="Flag this torrent"] {margin-left: 20px !important;}

.flagcontent ul {
    background: #60b0f0 !important;
    border-radius: 4px 4px 15px;
    width: 95.8%;
    color: white !important;
    box-shadow: 0 0 3px rgba(65,65,65,0.5);
    padding: 10px 10px 10px 25px !important; }
.flagcontent ul a, .inputulrules a {color: white !important; font-weight: bold !important;}

.tlistsn, .viewsn, .tlistln, .viewln {color: #111111 !important;} /* Seeders & Leechers */
}

