css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://boards.4chan.org'),
url-prefix('https://boards.4chan.org')
{


body {
	font-family: arial, helvetica, sans-serif;
	font-size: 10pt;
	background: #C00 url(http://i.imgur.com/jFgBQ.png) 99% 100% no-repeat fixed;
	color: #fff!important;
	padding-left: 5px;
	padding-right: 5px;
	margin-right: 0;
	margin-left: 0;
	margin-top: 5px;
}

td {
	font-size: 10pt;
	padding: 0;
	margin: 0;
}

td.reply {
	border: 1px solid #520000;
	border-left: none;
	border-top: none;
	padding: 2px;
}

blockquote {
	font-size: 10pt;
}

.doubledash {
	color: #FFF;
}

.inputtext {
	margin: 0;
	margin-right: 2px;
	padding: 1px 4px;
	border: 1px solid #aaa;
	outline: none;
	font-family: arial, helvetica, sans-serif;
	font-size: 10pt;
}

input.inputtext {
	height: 1.75em;
}

td>.inputtext {
	height: auto;
	padding-top: 2px;
	padding-bottom: 3px;
}

.inputtext:focus {
	border: 1px solid #ea8;
}

.logo img {
	border: 1px solid #800;
}

.logo b span {
	font-family: tahoma;
}

.logo span {
	font-size: 28px;
	letter-spacing: -2px;
}

hr {
	border: none;
	border-top: 1px solid #D9BFB7;
	height: 0;
}

.postblock {
	border: 1px solid #000;
	font-size: 10pt;
	padding: 0 5px;
	line-height: 20px;
}

.rules {
	font-size: 10pt;
}

.rules a {
	text-decoration: none;
}

form a img {
	margin-top: 3px;
}

iframe,.rotating {
	background: #F0E0D6;
	border-right: 1px solid #D9BFB7;
	border-bottom: 1px solid #D9BFB7;
}

.pages {
	border: none;
	background: #990000;
	border-right: 1px solid #D9BFB7;
	border-bottom: 1px solid #D9BFB7;
}

.pages td {
	border: none;
	padding: 1px 5px;
}

.pages td {
	color: #b86;
}

.pages td b {
	color: #800;
}

.deletebuttons {
	text-align: right;
}

.deletebuttons br {
	display: none;
}


a:hover {
	color: #D00;
}

a.quotelink {
	color: #b5c5ff!important;
}

.logo {
	clear: both;
	text-align: center;
	font-size: 24pt;
	color: #FFF;
	width: 100%;
}

form {
	margin-top: 0;
}

.rules {
	width: 468px;
	font-size: 10px;
}

.rules>li {
	list-style: none;
}

.rules>li:before {
	content: "\2022 \20";
}

.postblock {
	background: #990000;
	color: #FFF;
	font-weight: 800;
}

.footer {
	text-align: center;
	font-size: 12px;
}

.unkfunc {
	color: #ffb3b3;
}

.filesize {
	text-decoration: none;
}

td .filesize {
	display: inline;
	background: none;
}

.filesize span,span.postername,span.filetitle,span.commentpostername {
	unicode-bidi: embed;
}

.filetitle,.replytitle {
	background: inherit;
	color: #CC1105;
	font-weight: 800;
}

.postername,.commentpostername {
	background: inherit;
	color: #c4efc2;
	font-weight: 800;
}

.postertrip {
	background: inherit;
	color: #c4efc2;
}

.oldpost {
	background: inherit;
	color: #fff;
	font-weight: 800;
}

.omittedposts,.abbr {
	color: #fff;
}

.reply {
	background: #990000;
	color: #FFF;
}

.replyhl {
	background: #F0C0B0;
	color: #FFF;
}

.doubledash {
	vertical-align: top;
	clear: both;
	float: left;
}

a.quotejs:active,a.quotejs:link,a.quotejs:visited {
	color: #FFF;
	text-decoration: none;
}

a.quotejs:hover {
	color: #d00;
}

.tn_thread {
	width: 200px;
	height: 100px;
	margin: 0 20px 20px 20px;
	float: left;
	background: #eed;
	border: #ea8 1px solid;
	text-align: center;
}

.tn_reply {
	width: 100px;
	height: 100px;
	margin: 0 20px 20px 20px;
	float: left;
	background: #eed;
	border: #ea8 1px solid;
	text-align: center;
}

.adHeadline {
	font: bold 10pt serif;
	text-decoration: underline;
	color: #00e;
}

.adText {
	font: normal 10pt serif;
	text-decoration: none;
	color: #800;
}

#ad {
	width: 120px;
	margin: 0;
	padding: 0;
	position: absolute;
	right: 150px;
	border: 1px solid #EA8;
	font-family: arial, helvetica, sans-serif;
	font-size: 11px;
}

#ad div {
	margin: 0;
	padding: .4em;
}

#ad div.ad-title {
	padding: 0;
	background: #EA8;
	color: #FFF;
	font-size: 11px;
}

#ad div.ad-title a {
	font-family: arial, helvetica, sans-serif;
}

#ad div.ad-text a {
	font-family: arial, helvetica, sans-serif;
}

.bottomAdTitle {
	font-family: arial, helvetica, sans-serif;
	background: #EA8;
	color: #FFF;
}

#bottomAd {
	height: 52px;
	font-size: 11px;
}

#bottomAdOuter {
	width: 600px;
	border: 1px solid #EA8;
	font-size: 11px;
}

.spoiler a.quotelink,.spoiler .unkfunc {
	color: inherit;
}

.exif {
	display: none;
	font-family: arial, helvetica, sans-serif;
	font-size: xx-small;
}

.exif td {
	font-family: arial, helvetica, sans-serif;
	font-size: xx-small;
}

.exif td b {
	font-weight: 800;
	text-decoration: underline;
	font-size: x-small;
}

#header {
	position: absolute;
	top: 2px;
	left: 5px;
	right: 5px;
}

* html #header {
	width: 100%;
}

#navtop,#navbot {
	left: 0;
	float: left;
}

#navtopr,#navbotr {
	right: 0;
	display: block;
	float: right;
	text-align: right;
}

#header,#navbot,#navbotr {
	font-size: 9pt;
	color: #b86;
}

#header a,#navbot a,#navbotr a,.pages td a {
	font-weight: normal;
	padding: 1px;
	text-decoration: none;
	color: #800;
}

#header a:hover,#navbot a:hover,#navbotr a:hover,.pages td a:hover {
	color: #d00;
}

#footer {
	clear: both;
	padding-top: 10px;
}

#footer center font {
	font-size: 7pt;
}

.pages td a {
	color: #00e;
}

td.replyhl {
	border: 1px solid #520000;
	border-left: none;
	border-top: none;
	padding: 2px;
}

td.deletebuttons input.checkbox {
	margin: 1px 2px 1px 2px;
}

div.logo img {
	margin: 5px 0 5px 0;
}

.fstitle {
	float: left;
	width: 25px;
}

a {
	color: #ffd700!important;
}
}
