css:
@-moz-document url-prefix("http://histmag.org/") {
body {
    width: 93.75%;
}
#content  {
    width: 72.9166667%
}
#sidebar, #footer .blogs {
    width: 25%
}

#sidebar #panels .login, #sidebar #panels .register, #sidebar #panels .newsletter, #sidebar #panels .socialmedia {
width: 83.3333333333%;

}

#cse-search-box {
width: 91.6666666667%;
}

#footer .column {
    width: 20.8333333333%;
}

#footer .rss {
	width: 75%;
}

#footer .rss, #footer .blogs {
    box-sizing: border-box;
}

.social.top-social {
	display: none;
}

#article ul {
    display: inline-block;
}

#content, #article {
    font-size: 18px;
    line-height: 1.5;
   
}
}
