/*** Firebug ***/
.panelNode {
    background: #000C17 !important;
    color: #a6a6a6;
}

.panelNode * {
    font-size: 15px!important;
    font-family: "Consolas", "Courier New"!important;
    font-weight: normal!important;
}

.panelNode a {
    color: #f92672 !important;
}

.jumpHighlight {
    background-color: yellow !important;
}


/*** Console Panel ***/
.panelNode-console {
    background: #000C17 !important;
    font-family: "Consolas", "Courier New"!important;
    font-size: 12px!important;
}

.panelNode-console .logRow,
.panelNode-console .logGroupBody {
    background-color: transparent;
    border-color: #151515;
    color: #a6a6a6;
}

.objectBox-number,
.objectLink-styleRule,
.objectLink-element,
.objectLink-textNode {
    color: #a6a6a6;
}

.logRow-warn,
.logRow-warningMessage,
.logRow-warn .objectLink-sourceLink,
.logRow-warningMessage .objectLink-sourceLink {
    background-color: transparent;
}

.logRow-error,
.logRow-assert,
.logRow-errorMessage,
.logRow-error .objectLink-sourceLink,
.logRow-errorMessage .objectLink-sourceLink {
    background-color: transparent;
}

.logRow-info,
.logRow-info .objectLink-sourceLink {
    background-color: transparent;
}

.logRow-spy,
.logRow-spy .objectLink-sourceLink {
    background-color: #222;
}

.spyTitle {
    color: #75715e;
    background: #2a2a2a;
    border-radius: 20px;
    padding: 2px 10px;
}

.logRow-command > div > .logContent > .objectBox-text {
    color: #ffe792;
}

.logRow-profile > .logRow > .objectBox-text {
    color: #a6a6a6;
}

.logRow,
.logGroup,
.logGroupBody {
    background-color: #000C17 !important;
    border: none;
}

.logCounterValue {
    color: #000;
    font-size: 12px!important;
}

.panelNode-console .objectLink {
    background-color: #000c17;
    padding: 2px 5px;
}


/*** HTML Panel ***/
.panelNode-html {
    font-family: "Consolas", "Courier New"!important;
    background: #000C17 !important;
    font-size: 15px!important;
    font-weight: normal!important;
}

.panelNode-html * {
    font-family: "Consolas", "Courier New"!important;
    white-space: normal;
}

.panelNode-html .sourceLine {
    background: #000C17 !important;
    border-right: dotted 1px #3b3a32 !important;
}

.panelNode-html .highlighted .nodeLabel,
.panelNode-html .mutated,
.panelNode-html .mutated .nodeLabel {
    background: #000c17 !important;
    border-color: #1a1a1a !important;
    border-radius: 3px !important;
}

.nodeBox.highlighted > .nodeLabel,
.nodeBox.highlighted > .nodeLabel > .nodeLabelBox,
.nodeBox.highlighted > .nodeLabel > .nodeLabelBox > .nodeTag,
.nodeBox.highlighted > .nodeLabel > .nodeLabelBox > .nodeAttr > .nodeValue,
.nodeBox.highlighted > .nodeLabel > .nodeLabelBox > .nodeText,
