.panelNode-html .selected .twisty {
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAkAAAAJCAYAAADgkQYQAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAC5JREFUeNpi/P//PwMhwMRABMCm6D9ZJrHgMAHGZkRXxIikgJEqDmcky+EAAQYAqSsHE4fG1SoAAAAASUVORK5CYII=) transparent no-repeat 50% 50%!important;
}

.nodeComment {
    color: #0088ff!important;
}

.panelNode-html .selected {
    background-color: #2a2a2a;
    border-radius: 3px!important;
}

.nodeBox.selected > .nodeLabel > .nodeLabelBox,
.nodeBox.selected > .nodeLabel {
    /*background-color: #f92672!important;*/
    background-color: #000C37;
}

.nodeBox.mutated > .nodeLabel,
.nodeAttr.mutated,
.nodeValue.mutated,
.nodeText.mutated,
.nodeBox.mutated > .nodeText {
     color: #bf5656!important;
}

.nodeBox.selected.mutated > .nodeLabel,
.nodeBox.selected.mutated > .nodeLabel > .nodeLabelBox,
.nodeBox.selected > .nodeLabel > .nodeLabelBox > .nodeAttr.mutated > .nodeValue,
.nodeBox.selected > .nodeLabel > .nodeLabelBox > .nodeAttr > .nodeValue.mutated,
.nodeBox.selected > .nodeLabel > .nodeLabelBox > .nodeText.mutated {
    color: #bf5656!important;
}

/*** HTML > Style Panel ***/
