css:
@-moz-document domain("0-chan.ru"),
domain("0chan.so") {
    body {
        background-color: #6E7C87;
        color: #FFF;
        font-family: Calibri
    }
    
    a {
        color: #FFF;
    }
    a:visited {
        color: #FFF;
    }
    a:hover {
        color: #343855;
    }
    .adminbar {
        clear: both;
        float: right;
        font-size: .8em;
        /*float:center;*/
    }
    .adminbar a {
        font-weight: bold;
    }
    .theader,
    .passvalid,
    .replymode {
        background: #768998;
        text-align: center;
        color: #BDD2E7;
        clear: both;
        font-weight: 700;
        padding: 2px;
        margin-bottom: .5em;
        border: solid 1px #1B204D;
        -moz-border-radius: 5px;
        padding: 2px
    }
    .postblock {
        background: transparent;
        color: #D1D7DD;
        font-weight: bold;
    }
    .footer {
        text-align: center;
        font-size: 12px;
        font-family: arial;
        margin: 2em 0 0
    }
    .dellist {
        font-weight: 700;
        text-align: center
    }
    .delbuttons {
        text-align: center;
        padding-bottom: 4px
    }
    .managehead {
        background: #768998;
        color: #809EBB;
        padding: 0
    }
    .postlists {
        background: #FFF;
        width: 100%;
        color: maroon;
        padding: 0
    }
    .unkfunc {
        background: inherit;
        color: #81B1FF
    }
    .reflink {
        font-size: .8em;
        font-weight: bold;
        
    }
    .reflink a:hover {
        color:#343855;
    }
    .filesize {
        text-decoration: none;
        color: #FFF;
        font-size: .8em
    }
    .filetitle {
        background: inherit;
        font-size: 1.2em;
        color: #28344A;
        font-weight: 700;
    }
    .postername {
        color: #FFF;
        font-weight: 700;
    }
    .postertrip {
        color: #74B8D4;
    }
    .oldpost {
        color: #09F;
        font-weight: 700
    }
    .omittedposts {
        color: #D0D0D0
    }
    .reply {
        background: #546471;
        border: solid 2px #546471;
        -moz-border-radius: 5px;
        margin: 0;
        padding: 0;
    }
    blockquote {
        margin: .5em .5em .5em 1em;
    }
    blockquote p {
        margin: 0;
        padding: 0;
    }
    .reply blockquote {
        margin: .5em;
    }
    .doubledash {
        display: none;
        vertical-align: top;
        clear: both;
        float: left;
    }
    .replytitle {
        font-size: 1.2em;
        color: #FFF;
        font-weight: 700;
    }
    .commentpostername {
        color: #FFF;
        font-weight: 800;
    }
    .thumbnailmsg {
        font-size: .8em;
        color: #FFF;
    }
    hr {
        height: 3px;
        background: url(http://inach.org/css/heon/stripe1.png) center no-repeat;
        border-width: 0
    }
    .highlight {
        background: #212D57;
        color: #A19CBC;
        border: 2px dashed #585858;
    }
    textarea,
    select,
    input {
        -moz-box-sizing: content-box;
        -webkit-box-sizing: content-box;
        box-sizing: content-box;
        /*-moz-appearance:none;-webkit-appearance:none;appearance:none;*/
        
        background-color: #37414a;
        color: #fff;
        border: 2px solid #545454!important;
        -moz-border-radius: 5px;
        -webkit-border-radius: 5px;
        border-radius: 5px;
    }
    pre,
    code {
        font-size: 12px;
        font-family: monospace, sans-serif!important;
        background: #2C2C2C!important;
        color: #CCC!important;
        white-space: pre-wrap!important;
        word-wrap: break-word!important;
        overflow: auto!important;
        max-width: 80em!important;
        display: block!important;
    }
    pre a,
    code a {
        color: #FFF!important;
    }
    .cubscolor {
        color: #28344A;
    }
    a,
    .abbrev {
        color: #FFF;
    }
    .row1,
    .row2 {
        background: #343860;
        color: #FFF;
    }
    table,
    table td {
        border-style: none;
    }
    .rules {
        font-size: .7em;
    }
    div.hide480px a {
        font-weight: 700;
    }
    div.hide480px {
        font-size: 0.8em;
    }
    td.de-pview input[type="checkbox"] {
        margin-right: 7px;
    }
    .de-post-btns {
        display: table-cell;
        vertical-align: middle;
        padding-left: 5px;
    }
    span.de-ppanel {
        display: table-cell;
        vertical-align: middle;
        padding-left: 6px;
    }
    /*dollchan ext*/
    
    .de-pview blockquote {
        margin-left: 20px!important;
        margin-right: 20px!important;
        margin-top: 10px!important;
    }
    .de-post-counter:after {
        color: #57B659!important;
    }
    span.spoiler,
    span[onmouseout*="color='black'"] {
        background: #212121;
        color: #212121
    }
    span.spoiler:hover,
    span[onmouseover*="color='white'"]:hover {
        color: #48B0FD
    }
    span.spoiler a {
        color: #212121!important;
        background: #212121!important;
    }
    span.spoiler a:hover {
        background: #212121!important;
        color: white!important;
    }
    .catalogmode {
        background: #768998;
        text-align: center;
        color: #BDD2E7;
        clear: both;
        font-weight: 700;
        margin-bottom: .5em;
        border: solid 1px #1B204D;
        -moz-border-radius: 5px;
        padding: 2px
    }
    .content-background {
        background-color: #6E7C87;
    }
    .logo {
        clear: both;
        text-align: center;
        font-size: 2em;
        font-weight: bold;
        color: #C8CDD5;
        /*width:100%;*/
    }
.nothumb {
    color: #37414A;
    background-color: #FFF;
    border-style: dotted;
    margin: .3em .5em
}
#showPosts,
select {
    color: #FFFFFF!important;
}
#bottom {
    left: 143px;
    /*position:absolute;*/
}
.op-post .filesize {
    margin-left: 20px;
}
html,
body {
    margin: 0;
    padding: 0;
}
option {
    color: black!important;
}
pre {
    width: 100%;
}
.filesize-n {
    display: none;
}
.long-words td.reply {
    width: 100%;
}
.long-words td.reply img {
    max-width: 716px!important;
}
.long-words td.reply blockquote {
    margin-left: 20px;
    margin-right: 20px;
    margin-top: 10px;
}
table td.reply blockquote {
    margin-left: 20px;
    margin-right: 20px;
    margin-top: 10px;
}

.ui-button-text-icons,
.ui-button-text {background-color:#37414A;color:#FFFFFF;
    
}
}
