css:
@-moz-document domain("learnyousomeerlang.com") {
* {
  border-color: #444 !important;
  text-shadow: none !important;
  box-shadow: none !important;
}

body {
  background-color: #191919 !important;
  background-image: url(data:image/png;base64,) !important;
  background-clip: border-box !important;
  background-origin: padding-box !important;
  background-repeat: repeat !important;
  background-size: auto !important;
  background-position: left top !important;
  background-attachment: scroll !important;
  color: #bbbbbb !important;
}

blockquote {
  background: #191919 none !important;
}

/* === text selection === */
::selection {
  background: #4183C4 !important;
  color: #fff !important;
}
::-moz-selection {
  background: #4183C4 !important;
  color: #fff !important;
}

/* === input focus hightlight === */
input:focus {
  border-color: #4183C4 !important;
  box-shadow: 0px 1px 2px rgba(255, 255, 255, 0.075) inset,
              0px 0px 5px rgba(255, 255, 255, .075) !important;
}

textarea {
  background-color: #111 !important;
  color: #ddd !important;
}

/* === placeholder === */
::-webkit-input-placeholder {
  color: #bbb !important;
}
::-moz-placeholder {
  color: #bbb !important;
}

/* === text === */
#content td, #content p {
  text-align: justify;
}

/* === code === */
#content code {
  background-color: #454E32 ;
  color: #e0e8ff;
}
#content var {
  background-color: #4E3249 ;
  color: #e0e8ff;
}
body .syntaxhighlighter div.lines {
  background-color: #1b2426 !important;
}
body .syntaxhighlighter .line.alt1 .content {
  background-color: #1b2426 !important;
}
body .syntaxhighlighter .line.alt2 .content {
  background-color: #1b2426 !important;
}

/* === ? === */
#content td, #content p {
  color: inherit !important; /* from #565656; */
}
#content ul, #content ol {
  color: inherit !important; /* from #565656; */
}
#content dl {
  color: inherit !important; /* from #565656; */
}

/* === note === */
#content div.note {
  background-color: rgba(168, 168, 68, 0.25) !important;
  border-color: rgba(168, 168, 68, 0.3) !important;
}
#content div.note p {
  background: none !important;
}

/* === note.koolaid === */
#content div.koolaid {
  border-color: rgba(134, 100, 27, 0.3) !important;
  background-color: rgba(134, 100, 27, 0.25) !important;
}
#content div.koolaid p {
  background: none !important;
}

/* === grass === */
#grass {
  height: 0 !important;
}
}

