body .syntaxhighlighter {
  background-color: #1b2426 !important;
}
body .syntaxhighlighter .line.alt1 {
  background-color: #1b2426 !important;
}
body .syntaxhighlighter .line.alt2 {
  background-color: #1b2426 !important;
}
body .syntaxhighlighter .line.highlighted.alt1, body .syntaxhighlighter .line.highlighted.alt2 {
  background-color: #323e41 !important;
}
body .syntaxhighlighter .line.highlighted.number {
  color: #b9bdb6 !important;
}
body .syntaxhighlighter table caption {
  color: #b9bdb6 !important;
}
body .syntaxhighlighter .gutter {
  color: #afafaf !important;
}
body .syntaxhighlighter .gutter .line {
  border-right: 3px solid #435a5f !important;
}
body .syntaxhighlighter .gutter .line.highlighted {
  background-color: #435a5f !important;
  color: #1b2426 !important;
}
body .syntaxhighlighter.printing .line .content {
  border: none !important;
}
body .syntaxhighlighter.collapsed {
  overflow: visible !important;
}
body .syntaxhighlighter.collapsed .toolbar {
  color: #5ba1cf !important;
  background: black !important;
  border: 1px solid #435a5f !important;
}
body .syntaxhighlighter.collapsed .toolbar a {
  color: #5ba1cf !important;
}
body .syntaxhighlighter.collapsed .toolbar a:hover {
  color: #5ce638 !important;
}
body .syntaxhighlighter .toolbar {
  color: white !important;
  background: #435a5f !important;
  border: none !important;
}
body .syntaxhighlighter .toolbar a {
  color: white !important;
}
body .syntaxhighlighter .toolbar a:hover {
  color: #e0e8ff !important;
}
body .syntaxhighlighter .plain, body .syntaxhighlighter .plain a {
  color: #b9bdb6 !important;
}
body .syntaxhighlighter .comments, body .syntaxhighlighter .comments a {
  color: #878a85 !important;
}
body .syntaxhighlighter .string, body .syntaxhighlighter .string a {
  color: #5ce638 !important;
}
body .syntaxhighlighter .keyword {
  color: #5ba1cf !important;
}
body .syntaxhighlighter .preprocessor {
  color: #435a5f !important;
}
body .syntaxhighlighter .variable {
  color: #ffaa3e !important;
}
body .syntaxhighlighter .value {
  color: #009900 !important;
}
body .syntaxhighlighter .functions {
  color: #ffaa3e !important;
}
body .syntaxhighlighter .constants {
  color: #e0e8ff !important;
}
body .syntaxhighlighter .script {
  font-weight: bold !important;
  color: #5ba1cf !important;
  background-color: inherit !important;
}
body .syntaxhighlighter .color1, body .syntaxhighlighter .color1 a {
  color: #e0e8ff !important;
}
body .syntaxhighlighter .color2, body .syntaxhighlighter .color2 a {
  color: white !important;
}
body .syntaxhighlighter .color3, body .syntaxhighlighter .color3 a {
  color: #ffaa3e !important;
}
body .syntaxhighlighter .atoms {
  color: #88b !important;
}

}
