css:
@-moz-document domain("unrealengine.com") {
/*rgba(24,24,24,0.4) !important*/
* {
  border-color: #444 !important;
  text-shadow: none !important;
  box-shadow: none !important;
}

html {
  background-color: #111 !important;
}

body {
  background-color: #191919 !important;
  background-image: url(data:image/png;base64,) !important;
  background-clip: border-box !important;
  background-origin: padding-box !important;
  background-repeat: repeat !important;
  background-size: auto !important;
  background-position: left top !important;
  background-attachment: scroll !important;
  color: #bbbbbb !important;
}

/* === custom color === */
/*a {
  color: #4183C4 !important
}*/

/* === text selection === */
::selection {
  background: #4183C4 !important;
  color: #fff !important;
}
::-moz-selection {
  background: #4183C4 !important;
  color: #fff !important;
}

/* === input focus hightlight === */
input:focus {
  border-color: #4183C4 !important;
  box-shadow: 0px 1px 2px rgba(255, 255, 255, 0.075) inset,
              0px 0px 5px rgba(255, 255, 255, .075) !important;
}

textarea {
  background-color: #111 !important;
  color: #ddd !important;
}

/* === boxes === */

.tip {
  background-color: rgba(70, 234, 70, 0.25) !important;
  border-color: rgba(70, 234, 70, 0.3) !important;
}
.note {
  background-color: rgba(168, 168, 68, 0.25) !important;
  border-color: rgba(168, 168, 68, 0.3) !important;
}
.warning {
  background-color: rgba(134, 27, 27, 0.25) !important;
  border-color: rgba(134, 27, 27, 0.3) !important;
}


/* === code === */
html pre {
  background-color: rgba(238, 238, 238, 0.05) !important;
}
pre code {
  color: #bbb !important;
  padding: 1px 0 !important;
}
.comment-text pre code {
  background: none !important;
}
li.L1, li.L3, li.L5, li.L7, li.L9 {
  background-color: rgba(238, 238, 238, 0.025) !important;
}
/* === code highlight */

.pln { color: #dddddd } /* Plaintext */

@media screen {
  .nocode { background-color: inherit; color: #000 }
  .str { color: #a5c261 } /* string */
  .kwd { color: #cc7832 } /* keyword */
  .com { color: #808080 } /* comment (single-line, multi-line) */
  .typ { color: #dddddd /*#9876aa awful parser*/ } /* type */
  .lit { color: #49858b } /* literal (number?) */
  .pun { color: #a9b7c6 } /* punctuation */
  .opn, .clo { color: #660 } /* open and close brackets () {} [] */
  .tag { color: #008 } /* html/xml tag */
  /*.htm { color: #dda0dd; } /* html tag */
  /*.xsl { color: #d0a0d0; } /* xslt tag */
  .atn { color: #606 } /* attribute name */
  .atv { color: #080 } /* attribute value */
  .dec { color: #606 } /* a declaration */
  .var { color: #606 } /* a variable name */
  .fun { color: red } /* a function name */
}

/*@media print,projection {
  .str { color: #060 }
  .kwd { color: #cc7832; font-weight: bold }
  .com { color: #600; font-style: italic }
  .typ { color: #404; font-weight: bold }
  .lit { color: #044 }
  .pun,.opn,.clo { color: #440 }
  .tag { color: #006; font-weight: bold }
  .atn { color: #404 }
  .atv { color: #060 }
}*/
}

@-moz-document domain("answers.unrealengine.com") {
/*.answer-body a, .question-body a, .comment-text a*/
a {
  color: #4183C4 !important
}

.navbar .brand {
  color: #f7931d !important;
}
    
.navbar .nav>li>a {
  color: #999999 !important;
}
        
.resolved, .page-header h1, .welcome-bar h2, .span3 h3, .span9 h2 {
  color: #d8671c !important;
}

.accepted-answer, .accepted-answer .comments-container {
  background-color: rgba(0%, 90%, 0%, 0.1) !important;
}

/* === header and menu === */
.breadcrumb, .boxC {  
  background-color: transparent !important;
  background-image: none !important;
}

.answer, .answer-count-and-sort {
  border-bottom: 1px solid rgba(50%, 50%, 50%, 0.2);
}

.well {
  background-color: transparent !important;
}

/* === === */

.unanswered {
  background-color: rgba(238, 238, 238, 0.3) !important;
  color: #bbbbbb !important;
}
.answered {
  background-color: rgba(122, 122, 238, 0.3) !important;
  color: #aeaeda !important;
}
.answered-accepted, .answer-votes.answered-accepted {
  background-color: rgba(70, 238, 70, 0.3) !important;
  color: #4ed34e !important;
}

.short-summary .black {
  color: inherit !important;
}

/* === === */

.pagination a:hover, .pagination .active a {
  background-color: rgba(238, 238, 238, 0.2) !important;
}

.modal-footer {
  background-color: rgba(238, 238, 238, 0.1) !important;
}

html, blockquote, .modal, #wmd-button-bar, .wmd-button-bar {
  background: #191919 none !important;
}

#wmd-button-bar {
  border: 1px solid #ccc !important;
}

.wmd-button-row {
  background-color: transparent !important;
}

.wmd-button {
  background-color: transparent !important;
  background-image: url(http://StylishThemes.github.io/StackOverflow-Dark/images/wmd-buttons.png) !important;
}

.questions-count span,
label,
h1, h2, h3, h4, h5, h6 {
  color: #ddd !important;
}

.post-update-info-user a {
  display: inline-block;
  float: left;
}
.post-update-info-user:nth-child(3) {
  display: inline-block;
  float: left;
  margin-left: 5px;
  width: 150px;
  overflow: hidden;
  white-space: nowrap;
}
.post-update-info img {
  margin: 0 !important;
}
.post-update-info p {
  margin-top: 2px !important;
  margin-left: 5px !important;
  margin-bottom: 4px !important;
}

.form-actions {
  background-color: rgba(100%, 0%, 0%, 0.1) !important;
}

/* === badges === */
a.badge[title^="bronze"], a.badge-tag[title^="bronze"], .bronze, .bronze + .badgecount {
  color: #cc9966 !important;
}
a.badge[title^="silver"], a.badge-tag[title^="silver"], .silver, .silver + .badgecount {
  color: #c5c5c5 !important;
}
                                                    .gold, .gold + .badgecount,
a.badge[title^="gold"], a.badge-tag[title^="gold"], .badge1, .badge1 + .badgecount {
  color: #ffcc46 !important;
}
.badge-tag {
  background-color: #191919 !important;
  border-color: #111 !important;
}

.comment.nsc .comment-header {
  background-color: transparent !important;
}
.comment-user-gravatar, .comment-user.userinfo {
  float: left !important;
}
.comment-header .staff {
  margin-left: 5px !important;
  background-color: rgba(238, 238, 238, 0.5) !important;
}

.comment-text, .score {
  color: inherit !important;
  padding: 1px !important;
}

a.medal,
.tags a, span.tag {
  background: #222 !important;
  color: #ddd !important;
  -webkit-border-radius: 3px !important;
  border-radius: 3px !important;
  border-top: 1px solid #5a5a5a !important;
  border-left: 1px solid #5a5a5a !important;
  border-bottom: 1px solid #333 !important;
  border-right: 1px solid #333 !important;
}
a.post-vote {
  background: url("data:image/svg+xml;utf8, <svg version=\'1.1\' xmlns=\'http://www.w3.org/2000/svg\'><polygon points=\'1,19 12,5 23,19\' fill=\'rgba(0%,100%,0%,0.5)\' /></svg>") no-repeat scroll center center transparent !important;
  -webkit-filter: grayscale(1) brightness(0.75);
}
a.post-vote.down {
  background: url("data:image/svg+xml;utf8, <svg version=\'1.1\' xmlns=\'http://www.w3.org/2000/svg\'><polygon points=\'1,5 12,19 23,5\' fill=\'rgba(100%,0%,0%,0.5)\' /></svg>") no-repeat scroll center center transparent !important;
  -webkit-filter: grayscale(1) brightness(2.5);
  /*transform: rotate(180deg);*/
}
a.post-vote.on, a.post-vote:hover {
  -webkit-filter: none;
}
          
/*a.post-vote.up.on, a.post-vote.up:hover {
  background: url("data:image/svg+xml;utf8, <svg version=\'1.1\' xmlns=\'http://www.w3.org/2000/svg\'><polygon points=\'1,19 12,5 23,19\' fill=\'rgba(0%,100%,0%,0.5)\' /></svg>") no-repeat scroll center center transparent !important;
}
a.post-vote.down.on, a.post-vote.down:hover {
  background: url("data:image/svg+xml;utf8, <svg version=\'1.1\' xmlns=\'http://www.w3.org/2000/svg\'><polygon points=\'1,19 12,5 23,19\' fill=\'rgba(100%,0%,0%,0.5)\' /></svg>") no-repeat scroll center center transparent !important;
}*/

/* === user stats popuover === */
.popover-content, .popover-title {
  background-color: rgba(40, 40, 40, 0.8) !important;
}

#listA, .tabsA, .tabBar {
  background: none !important;
}

/* === inline code === */
code {
  background-color: #ccc !important;
}
}

@-moz-document domain("docs.unrealengine.com") {
html {
  background: #191919 none !important;
}

#navWrapper #navPanel, /* hackish selector for getting rid of "!important" in "#navPanel" style */
#maincol {  
  background-color: rgba(238, 238, 238, 0.025) !important;
}

.topics .item, .topics .description, .full_feature .description,
#legal,
#navPanel li,
.searchwrapper,
td {
  background-color: transparent !important;
}

.section,
.topic .wrapper .largetitle.topicbutton a,
.features .role .inner,
.member-list th {
  background-color: rgba(238, 238, 238, 0.02) !important;
}

.buttonlist li a,
.simplecode_api {
  background-color: rgba(238, 238, 238, 0.01) !important;
}

.largetitle,
.buttonlist li,
.topics .description, .full_feature .description,
.member-list .normal-row .desc-cell,
.member-list th,
#legal,
.features .role .inner {
