}

/* === fix harcoded height === */
.third .description {
  height: auto !important;
}

th {
  background-color: rgba(238, 238, 238, 0.03) !important;
  color: #ddd !important;
}

h1, h2,
.feature_section .title,
.feature_section .title a {
  color: #f7931d !important;
}
h3, h4 {
  color: #d8671c !important;
}
h5, h6 {
  color: #c25e1c !important;
}

pre {
  border-color: rgba(234, 234, 234, 0.1) !important;
}

#footer #copyright a,
#footer #terms a {
  color: #aaa !important;
}

td code {
  color: #eec !important;
}

.searchbox {
  color:  #bbb;
  background-color: rgba(192, 192, 192, 0.1) !important;
}

.searchterm_button {
  background-color: #333;
