}

body .features .inner .description {
  color:  #bbb;
}

body .features .title, .features .title a {
  color:  #1FA2FF;
}

.skill_tag_filter, .version_tag_filter {
  background-color: #333;
}

.normaltitle {
  color:  #ccc !important;
}

body .videonav .navigation .button.back a {
  background-color: rgba(11, 11, 1, 0.8);
}
body .videonav .navigation .button.back a:hover {
  background-color: rgba(0, 96, 128, 0.8);
}

.buttonlist li .titlesection {
