css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mobilarena.hu") {

/*
for PH!mod: "sky blue" ver. 0.8.5 by sz.balazs.95
created: 2015.10.09 - 2015.10.29
published: 2015.11.01

img-blue: #29007f;
txt-blue: #00c0ff;
light gray: #939598;
gray: #58595B;
dark gray: #333333;
*/

#head .logo {width: 320px;left: 17px;}
#head .logo a {outline: 0px none;background: transparent url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/bannera-mobilarena-1.1.png) no-repeat scroll 50% 50%;}
#head .logo a span {color: transparent;}
#network li.ma {background: transparent url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/redcube-2.0.png) 0 0 no-repeat;}
#network li.ph, #network li.it, #network li.gp, #network li.lo, #network li.sg {background-image: url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/rcm-1.0.png);}
img[src="/design/headicon-rss_ma.gif"] {width: 0px !important;height: 0px !important;background: #29007f url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/rss-1.2.png) no-repeat scroll 0% 0%;padding: 0px 0px 19px 19px;}
.forumok .for_17 .title a, .forumok .for_18 .title a, .forumok .for_33 .title a, .forumok .for_57 .title a, .forumok .for_59 .title a, #left .sections .sec_30 a, #left .sections .sec_125 a, #left .sections .sec_140 a {color: #00c0ff;}
#main .boxl_coloured h4{background: #29007f url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/menu-m-1.0.png) left center no-repeat;color:#fff;text-align: center;font-size: 13px;font-weight: bold;}
.relcnt h4 {font-size: 18px;color: #000;background: transparent url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/blackline-1.1.png) repeat-x scroll 0px 16px;}
.relcmp h4 {font-size: 18px;color: #000;background: transparent url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/blackline-1.1.png) repeat-x scroll 0px 15px;}
}

@-moz-document domain("m.mobilarena.hu") {

#head {border-bottom: 0.4em solid #29007f;background: #000 url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/background-ma-1.2.png) no-repeat scroll 0px 0px;height: 3.9em;}
#head div.logo a img {display: none;}
}
