css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("logout.hu") {

/*
for PH!mod: "sky blue" ver. 0.8.5 by sz.balazs.95
created: 2015.10.14 - 2015.10.29
published: 2015.11.01

img-blue: #29007f;
txt-blue: #00c0ff;
light gray: #939598;
gray: #58595b;
dark gray: #333333;
*/

a { color: #00c0ff;}
#head .logo {width: 220px; left: 67px;}
#head .logo a {outline: 0px none; background: transparent url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/bannera-logout-1.1.png) no-repeat scroll 50% 50%;}
#head .logo a span {color: transparent;}
#network li.lo {background: transparent url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/redcube-2.0.png) 0 0 no-repeat;}
#network li.ph, #network li.it, #network li.gp, #network li.ma, #network li.sg {background-image: url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/rcm-1.0.png);}
.border {border: 1px solid #f3f3f3;}
img[src="/design/headicon-rss_lo.gif"], img[src="/design/headicon-rss_ph.gif"] {width: 0px !important; height: 0px !important; background: #29007f url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/rss-1.2.png) no-repeat scroll 0% 0%; padding: 0px 0px 19px 19px;}
.forumok .for_52 .title a, .forumok .for_53 .title a, .forumok .for_54 .title a {color: #00c0ff;}
#right .author h4 {background: #29007f url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/bcg-auth-1.2.png) no-repeat scroll 100% 0%;}
#right .blogs h4 {background: #29007f url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/bcg-auth-1.2.png) no-repeat scroll 100% -7px; color: #fff;}
#right .author p {background: #000;}
#right .blogs li {background: #939598;}
.toplist h4 {color: #fff; background-color: #29007f;}
.toplist ul {background: #f3f3f3;}
var, var a, .blogarch .mod, .mod {color: #00c0ff !important;}
.boxl_gray h4 {background: #29007f url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/lhead-1.1.png) no-repeat scroll left center; color: #fff;}
#right .author ul, .toplist ul {background: #333333;}
.menu_rm ul {margin-top: 220px;}
.menu_rm_usr a, .menu_rm_usr .rm_4 {background-image: url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/menu-in-1.1.png) !important; background-color: #29007f;}
.menu_rm a, .menu_rm .rm_4 {background-image: url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/menu-out-1.1.png); background-color: #29007f;}
.menu_rm .rm_4 a {background: #000 !important;}
#navi {background: #000 url(http:users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/background-v0.3.1.png) no-repeat scroll -17px 0px; text-align: left;}
#navi li, #navi li.te, #navi li.hi, #navi li.ci, #navi li.fo {background: transparent;}
#navi li.te.act, #navi li.hi.act, #navi li.ci.act, #navi li.fo.act {background: transparent url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/active-s-1.0.png) no-repeat scroll 100% 18px;}
#navi ul.menu a, #navi li.sr li a {padding-top: 8px; height: 16px; color: #00c0ff;}
#navi ul.menu {background: transparent;}
#main #navi{height: 26px;}
.cntcol h6 a {color: #00c0ff;}
.blogarch li {background: transparent;}
.blogarch li.atc p > span.type, .blogarch .feat p > span.type {background-color: #00c0ff;}
.blogarch small {color: #58595b;}
.blogarch .feat h1 a {color: #00c0ff;}
.anyagok h4 {color: #00c0ff; font-size: 16px;}
#main .boxl_coloured h4 {background: #29007f url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/menu-m-1.0.png) left center no-repeat; color:#fff; text-align: left; font-size: 13px; font-weight: bold;}
.blogarch p > span.msgs {background: transparent url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/msg-icon-1.1.png) no-repeat scroll 3px 3px;}
.blogarch p > span.time, .blogarch p > span.msgs a {color: #000;}
.blogarch li.atc {background: transparent;}
.blogarch li.atc h1 a {color: #000;}
.blogarch li.atc p a {color: #000; padding-top: 8px; font-size: 12px;}
.relcnt h4 {font-size: 18px; color: #000; background: transparent url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/blackline-1.1.png) repeat-x scroll 0px 14px;}
.relcmp h4 {font-size: 18px; color: #000; background: transparent url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/blackline-1.1.png) repeat-x scroll 0px 13px;}
.nwcomm_mid {background: #58595b;}
.nwcomm_mid h4 {color: #00c0ff;}
}

@-moz-document domain("m.logout.hu") {

#head {border-bottom: 0.4em solid #29007f; background: #000 url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/background-lo-1.2.png) no-repeat scroll 0px 0px; height: 3.9em;}
#head div.logo a img {display: none;}
}
