css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("itcafe.hu") {

/*
for PH!mod: "sky blue" ver. 0.8.5 by sz.balazs.95
created: 2015.10.16 - 2015.10.29
published: 2015.11.01

img-blue: #29007f;
txt-blue: #00c0ff;
light gray: #939598;
gray: #58595B;
dark gray: #333333;
*/

#head .logo {width: 200px;left: 77px}
#head .logo a {outline: 0px none;background: transparent url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/bannera-itcafe-1.2.png) no-repeat scroll 50% 50%}
#head .logo a span {color: transparent}
#network li.it {background: transparent url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/redcube-2.0.png) 0 0 no-repeat}
#network li.ph, #network li.ma, #network li.gp, #network li.lo, #network li.sg {background-image: url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/rcm-1.0.png)}
img[src="/design/headicon-rss_it.gif"] {width: 0px !important;height: 0px !important;background: #29007f url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/rss-1.2.png) no-repeat scroll 0% 0%;padding: 0px 0px 19px 19px}
#main .boxl_coloured h4{background: #29007f url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/menu-m-1.0.png) left center no-repeat;color:#fff;text-align: center;font-size: 13px;font-weight: bold}
#right .history {background: #000}
#right .history h4 {background-color: #29007f;color: #fff}
#right .history .aright {background-position: -80px center !important}
#right .history .aright:hover {background-position: -100px center !important}
#right .history .aright.inact {background-position: -60px center !important}
#right .history .aleft {background-position: -20px center !important}
#right .history .aleft:hover {background-position: -40px center !important}
#right .history .aleft.inact {background-position: 0px center !important}
#right .history .aleft, #right .history .aright {background: transparent url("http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/it-arw-1.0.png") no-repeat scroll 0% 0%}
#right .history .tv div h2 a {color: #00c0ff}
#right .thrcomm::after {background: #58595b}
#right .thrcomm h4 {background: #29007f}
.relcnt h4 {font-size: 18px;color: #000;background: transparent url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/blackline-1.1.png) repeat-x scroll 0px 16px}
.relcmp h4 {font-size: 18px;color: #000;background: transparent url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/blackline-1.1.png) repeat-x scroll 0px 14px}
}

@-moz-document domain("m.itcafe.hu") {

#head {border-bottom: 0.4em solid #29007f;background: #000 url(http://users.atw.hu/b95/css/PH!mod/skyblue/0.8.5/background-it-1.2.png) no-repeat scroll 0px 0px;height: 3.9em}
#head div.logo a img {display: none}
}
