css:
/* i really want this to be global */

body {
    font-size: 15px;
	color: #eee;
    border-top: none;
    background: #303030;
    background-image: url('http://www.transparenttextures.com/patterns/az-subtle.png');}
*:not(.fa), p, body, h1, h2, h3, h4, h5, h6, .back-to-top, .socials p, #profileheading, #socialheading, .forumpost .subject, .block_login label{
    font-family: 'Roboto', sans-serif !important;
}
.block, .course-content ul li.section.main, #section-0 {
    padding: 8px;
    border-radius: 2px;
    border-top: 1px solid #505050;
    border-left: 1px solid #474747;
    background-color: #424242 !important;
    box-shadow: 0 0 11px 4px #252525;
    border-right: none;
    transition: 0.3s all;
    border-bottom: none;
    margin-bottom: 23px;
}
.course-content ul li.section.main.current{
    background-color: #555 !important;
    box-shadow: 0 0 18px 3px #111;
    border-top: 1px solid #999;
    transform: scale(1.05);
    border-left: 1px solid #777;
    border-right: 1px solid #777;
    border-radius: 5px;  
}
.course-content ul li.section.main.clearfix.current .content{
    box-shadow: none;
    border-radius: 0;
}
.block .header .title h2, .block h3.main {
    color: #fff !important;
    text-shadow: none !important;
}
/* Текст */
.forumpost .subject{
	color: #eee !important;
}
/* ЗАГОЛОВКИ, ІКОНКИ */
.block .header .title h2:before, .block.block_iconic_html .header .title h2 span.fa:before, p.tree_item.branch:before, #frontpage-category-combo h2, #frontpage-category-names h2, #frontpage-course-list h2, #site-news-forum h2, h1, h2, h3, h4, h5, h6, #adminsettings h3, p.tree_item.leaf a>i, .navbar .navbar-inner .nav > li.dropdown ul.dropdown-menu > li > a i {
    color: #CECECE !important;
}
/* ПОСИЛАНЯ */
a, a:visited, p.tree_item.branch {
    color: #bbb !important;
    transition: 0.4s all !important;
}
a:hover, .navbar .navbar-inner a:hover, p.tree_item.branch:hover{
    color: #fff !important;
}
.navbar .navbar-inner a{color: #eee !important;}
.forumpost .subject {
    font-weight: 600;
    line-height: 1.8em;
}
.back-to-top{
    background-color: #ccc;
    border-radius: 999px;
    width: 40px;
    height: 40px;
    display: block;
    line-height: 40px;
    padding: 0;
    box-shadow: 0 2px 6px 3px #111;
}
.back-to-top:hover{
    background-color: #eee;   
    width: 42px;
    height: 42px;
    line-height: 42px;
    box-shadow: 0 3px 9px 4px #111;
    margin-top: -5px;
    margin-right: -2px;
}
/* HEADER AND PAGE */
#page-header{
	display:none;
}
.navbar, .navbar-inner {background-color: #212121;box-shadow: 0 0 29px 3px #131313;}
.navbar .navbar-inner .nav > li.dropdown a:hover, .navbar .navbar-inner .nav>li>a:hover {
    box-shadow: inset 0 0 10px 1px #212121;
    background-color: #303030;
}
.navbar .navbar-inner #custom_menu_themecolours{display:none;}
.navbar .navbar-inner .dropdown-menu {
    border: 1px solid #333;
    border-radius: 3px;
    background-color: #303030;
    box-shadow: 0 0 13px 2px #212121;
    margin-top: 0px;
}
.navbar .navbar-inner .nav > li.dropdown ul.dropdown-menu>li>a:hover {
    background-color: #292929;
}
.navbar .navbar-inner .usermenu .dropdown img.userpicture {
    border: 1px solid #000;
    box-shadow: none;
    border-radius: 999px;
}
#page, .pagelayout-redirect{
 	padding-top: 60px;   
}
#page #page-content #region-main{
    float: right;
}
#page #page-content #region-main, #page #page-content div[role=main], .pagelayout-redirect #page-content #region-main, .pagelayout-redirect #page-content div[role=main]{
        border: none;
    background-color: transparent;
}
.forumheaderlist, .forumpost {
    background: rgba(0, 0, 0, 0.33);
    border-radius: 4px;
    border: 1px solid rgba(225, 225, 225, 0)!important;
}
.course_category_tree .category>.info{
 	background: rgba(0, 0, 0, 0.33);   
}

/* BREADCRAMBS */
.breadcrumb-nav ul.breadcrumb.style1 li {
    background-color: #212121;
}
.breadcrumb-nav ul.breadcrumb.style1 li:after {
    border-left-color: #212121;
}
/* LOGIN BLOCK */
.block_login .content .footer a, .block_login .content form input[type=submit]{
    border-color: #1C213C;
}
.block_login .content form input[type="submit"] {
    background-color: #394B6B;
}
.block_login .content .footer div a {
    background: #1C213C;
}
.block_login .content .footer div a:hover {
    background: #1F2B61;
}
.block_login .content .footer a:hover, .block_login .content form input[type=submit]:hover {
    background: #375488;
}
/* SHCHELUDE BLOCK */
#yui_3_17_2_2_1446066135766_345 a > span span{
    color: #333 !important;
}
#yui_3_17_2_2_1446066135766_345 a > span{
	background-color: #e6e6e6 !important;
    color: #333 !important; 
    display: block;
    padding: 4px 12px;
    font-size: 14px;
    line-height: 20px;
    text-align: center;
    cursor: pointer;
    text-shadow: 0 1px 1px rgba(255,255,255,.75);
    background-image: -moz-linear-gradient(top,#fff,#e6e6e6);
    background-image: -webkit-gradient(linear,0 0,0 100%,from(#fff),to(#e6e6e6));
    background-image: -webkit-linear-gradient(top,#fff,#e6e6e6);
    background-image: -o-linear-gradient(top,#fff,#e6e6e6);
    background-image: linear-gradient(to bottom,#fff,#e6e6e6);
    background-repeat: repeat-x;
    border: 1px solid #ccc;
    border-bottom-color: #b3b3b3;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 0 rgba(255,255,255,.2),0 1px 2px rgba(0,0,0,.05);
    -moz-box-shadow: inset 0 1px 0 rgba(255,255,255,.2),0 1px 2px rgba(0,0,0,.05);
    box-shadow: inset 0 1px 0 rgba(255,255,255,.2),0 1px 2px rgba(0,0,0,.05);
}
#yui_3_17_2_2_1446066135766_345 a > span:hover{
    text-decoration: none;
    background-position: 0 -15px;
    -webkit-transition: background-position .1s linear;
    -moz-transition: background-position .1s linear;
    -o-transition: background-position .1s linear;
    transition: background-position .1s linear;
}


#block-region-side-pre{
 	margin-left: 0 !important;
    float:left;
}

.bor{
    display: none;
}
hr {
    margin: 10px 0;
    border: 0;
    border-top: none;
    border-bottom: none;
    background-color: #303030;
    height: 2px;
}
ul.weeks h3.sectionname {
    white-space: nowrap;
    margin: 0;
    line-height: 24px;
    font-size: 12px;
    font-weight: 100;
    border-bottom: 2px solid #666;
    opacity: 0.5;
}
.coursebox {
    margin-bottom: 20px;
    border: none;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
}
.block_course_overview .coursebox {
    padding: 5px;
}
.block_course_overview .content h2.title {
    line-height: 20px;
    margin: 0;
}
/* fix blue color in courses */
span {
    color: #eee !important;
}
