css:
@-moz-document domain("readmanga.me"), domain("adultmanga.ru"), domain("grouple.ru"), domain("selfmanga.ru") {
body {
	font-family: 'San Francisco', sans-serif;
    font-weight: normal;
    background-color: #EEEEEE;
    color: #212121;
    padding-top: 50px;
}

* {
	text-shadow: none;
}

a {
	font-weight: normal;
    color: #2980b9;
    transition: ease color .3s;
}

a:hover {
	background-color: transparent;
    color: #34495e;
}

.person-link:visited, .element-link:visited, .manga-link:visited, a:visited {
	color: #2980b9;
}

.person-link:visited:hover, .element-link:visited:hover, .manga-link:visited:hover, a:visited:hover {
	background: none;
    color: #34495e;
}

.navbar-default {
	background-image: none;
    background-color: rgba(230, 126, 34,.8);
    position: fixed;
    top: 0;
    width: 100%;
    z-index: 999;
}

.navbar-default * {
	text-shadow: none;
}

.navbar-form .form-control {
	background-color: rgba(255,255,255,.5);
    box-shadow: none;
    border-color: rgba(255,255,255,.2);
    transition: ease all .3s;
}

.navbar-default .nav.navbar-nav li a {
	color: white;
}

.navbar-default .nav.navbar-nav li a:hover {
	color: rgba(255,255,255,.7);
}

.dropdown-menu {
	background-color: #34495e;
    border: none;
    padding: 0;
}

.dropdown-menu > li > a {
	padding: 8px 14px;
}

.dropdown-menu > li > a:hover {
	background: #2c3e50;
    color: white;
}

.dropdown-menu li.divider {
	margin: 0;
    background: #2c3e50;
}

.navbar-form .form-control:focus {
	border-color: #2c3e50;
    background: #fff;
}

.navbar-form .form-control::-webkit-input-placeholder {
    color: rgba(255,255,255,.9);
}

.navbar-form .btn {
	background: #34495e;
    color: white;
    border-color: #2c3e50;
    transition: ease border .3s, ease background .3s;
}

.navbar-form .btn:hover {
	border-color: #2980b9;
    background: #3498db;
}

.btn {
	background: #34495e;
    color: white;
    border-color: #2c3e50;
    transition: ease border .3s, ease background .3s;
    text-shadow: none;
}

.btn:hover {
	border-color: #2980b9;
    background: #3498db;
    color: white;
}

.btn:hover:active {
	background: #2980b9;
    color: white;
    outline: 0 none;
    border-color: #2980b9;
}

.btn:active {
	background: #2980b9;
    color: white;
    outline: 0 none;
}


.topControl {
    margin-left: 0px;    
    margin-right: 0px;
	padding: 10px 20px;
    background: #fff;
    border-radius: 3px;
    border: solid 1px #E0E0E0;

}

.form-control {
	background: #fafafa;
    box-shadow: none;
    border-color: #bdc3c7;
}

.form-control:focus {
	box-shadow: none;
    border-color: #34495e;
}

.no-user-select {
	background: #fff;
    padding: 10px;
    border-radius: 3px;
    border: solid 1px #E0E0E0;
}

.second-nav {
	display: none;
}

.federation-nav {
	position: fixed;
    background: rgba(52, 73, 94,.9);
    padding: 0;
    height: 30px;
    line-height: 30px;
}

.federation-nav a {
	color: white;
}

.federation-nav a:hover {
	color: rgba(255,255,255,.7);
}

.leftContent > *:first-child {
    display: none;
}
.top-manga-updates, .manga_ba, .add-manga-main  {
	display: none;
}
	
.mainNews > *:nth-child(1) {
	display: none;
}

.mainNews > *:nth-child(2) {
	display: none;
}

.pageBlock {
	border-radius: 0;
    border: none;
    box-shadow: none;
    background: transparent;
}

.pageBlock > h1 {
	margin-bottom: 30px;
}

.panel.news {
	box-shadow: none;
    border-color: #E0E0E0;
}

.panel.news .panel-footer {
	background: #F5F5F5;
    border-color: #E0E0E0;
}

.panel.news .panel-footer .btn {
	background: transparent;
    border: none;
    color: #616161;
    font-weight: 100;
    transition: ease color .3s;
}

.panel.news .panel-footer .btn:hover {
	color: #e67e22;
}

.newChapters, .chapters-link {
	background: #fff;
    border-radius: 3px;
    border: solid 1px #E0E0E0;
    
}

.newChapters thead h3, .chapters-link thead h3 {
	font-size: 18px;
    padding: 5px 10px;
    margin: 0;
    border-color: #E0E0E0;
}

.newChapters tbody tr:hover, .chapters-link tbody tr:hover {
	background: #fafafa;
}

.newChapters tbody tr td, .chapters-link tbody tr td {
	padding: 10px 20px;
}

.newChapters tbody tr td:hover, .chapters-lin tbody tr td:hoverk {
	background: #fafafa;
}

.pagination .step, .pagination .nextLink {
	background: #fafafa;
    border-color: #E0E0E0;
    color: #212121;
    transition: ease all .3s;
}

.pagination .step:hover, .pagination .nextLink:hover {
	color: white;
    border-color: #3498db;
    background: #3498db;
}

.pagination .currentStep {
	background: #fff;
    border-color: #E0E0E0;
    color: rgba(0,0,0,.4);
}

.pagination .currentStep:hover {
	background: #fff;
}

.twitter {
	background: #fff;
    border: solid 1px #e0e0e0;
    border-radius: 3px;
    margin: 0;
}

.twitter h5 {
	padding: 10px;
    margin: 0;
    border-bottom: solid 1px #e0e0e0;
}

.twitter > div[class*="cm"] {
	display: table;
}

.twitter > div[class*="cm"] > div {
	display: table-row;
    width: 100%;
    color: white;
    border: solid 1px #e0e0e0;
}

.twitter > div[class*="cm"]  div:nth-child(2n-1) {
	background: #fafafa;
}

.twitter > div[class*="cm"] > div > a, .twitter > *[class*="cm_"] > div > span {
	display: table-cell;
    padding: 10px;
    vertical-align: middle;
}

.twitter > div[class*="cm"] > div > a {
	width: 20%;
    border-right: solid 1px #E0E0E0;
    text-align: right;
    transition: ease border .3s;
}

.twitter > div[class*="cm"] > div > a:hover {
	border-color: #3498db;
}

.twitter > div[class*="cm"] > div > span {
	width: 80%;
    color: #212121;
}

.twitter div[class*="cm"].hide {
	display: none;
}

.translator-control {
    margin-left: 0px;
    margin-right: 0px;
	padding: 10px;
    background: #fff;
    border-radius: 3px;
}

.my-tooltip {
    border-color: #e0e0e0;
    border-width: 1px;
    box-shadow: none;
    background: #fff;
}

.js-link {
	color: #3498db;
}

.js-link:hover {
	color: #34495e;
    background: transparent;
}

.posts-container {
	background: #fff;
    padding: 10px;
    border-radius: 3px;
    border: solid 1px #e0e0e0;
}

.rightContent {
	margin: 0;
    padding-left: 5px;
    padding-right: 5px;
    padding-top: 0px;
    padding-bottom: 0px;
}

.rightContent > h5 {
	padding: 0;
    margin: 0;
}

.rightContent > h5 a {
	display: block;
    background: rgba(52, 152, 219,1.0);
    color: white;
    margin-bottom: 10px;
    padding: 7px 12px;
    border-radius: 3px;
    border: solid 1px rgba(41, 128, 185,1.0);
    transition: ease all .3s;
}

.rightContent > h5 a:hover {
	background: #34495e;
    border-color: #2c3e50;
}

.rightBlock {
	background: #fff;
    border-radius: 3px;
    border: solid 1px #e0e0e0;
    overflow: hidden;
}


.rightBlock h5 {
	padding: 10px;
    border-bottom: solid 1px #e0e0e0;
    background: #fafafa;
    margin: 0;
}

.rightContent .rightBlock > *:nth-child(2):not(ol) {
	padding: 10px;
}

.rightContent .rightBlock > .mangaList {
	margin: 15px;
}

.news-full {
	padding: 10px 15px;
    background: #fff;
    border-radius: 3px;
    border: solid 1px #e0e0e0;
    margin-bottom: 15px;
}

.forumline {
	border: none;
    background: none;
    color: #212121;
    border-spacing: 0;
}

th.thcornerl, th.thleft, th.thtop, th.thcornerr {
	border-radius: 0; 
    background: #f5f5f5;
    padding: 10px 20px;
    height: auto;
}

th.thbottom, th.thsides, th.thhead, td.cat, td.cathead, td.catsides, td.cathead, td.catleft, td.catright, td.catbottom {
	background: #fafafa;
    height: auto;
    padding: 7px 14px;
    border: none;
}

.navbar-brand > img {
    height: 35px;
    width: auto;
    
	margin-top: 5px;
}
}

@-moz-document url-prefix("http://grouple.ru/forum/") {
body {
	background: #ecf0f1;
}

.forumline {
	background: #fff;
}

th.thcornerl, th.thleft, th.thtop, th.thcornerr {
	background: #34495e;
    color: #fff;
    padding-top: 13px;
    padding-bottom: 13px;
}

th.thcornerl a, th.thleft a, th.thtop a, th.thcornerr a {
	color: #fff;
    border-bottom: dashed 1px #fff;
    transition: ease border .22s;
}

th.thcornerl a:hover, th.thleft a:hover:hover, th.thtop a:hover:hover:hover, th.thcornerr a:hover {
	border-color: #34495e;
}

th.thbottom, th.thsides, th.thhead, td.cat, td.cathead, td.catsides, td.cathead, td.catleft, td.catright, td.catbottom {
	background: #3498db;
    padding-top: 10px;
    padding-bottom: 10px;
    border: none;

}

.cattitle {
	color: #fff;
}

td.row2 {
	background: transparent;
}

.pageBlock > h1 {
	color: #212121;
    font-size: 20px;
    margin: 0;
    padding: 7px 10px;
    background-color: #fff;
    border: solid 1px #e0e0e0;
    border-radius: 3px;
}

.pageBlock > h1 a {
	float: right;
}

.pageBlock > p {
	display: none;
}

.forum-breadcrumb {
	margin: 5px 0;
    color: transparent;
}

.forum-breadcrumb a:after, .forum-breadcrumb span:after {
	content: "/";
    margin-left: 15px;
}

.forum-breadcrumb a:last-child:after, forum-breadcrumb span:last-child:after {
	display: none;
}

.moderation_highlight, .bg_small_yellow {
	background: transparent;
}

.bg_small_yellow {
	border-bottom: solid 1px #e0e0e0;
}

.bg_small_yellow:nth-child(2n) {
	background-color: rgba(0,0,0,.05);
}

.forumline>*>tr>td, .forumline {
	border: none;
}

td.row3announce, td.row3sticky, td.row2announce, td.row2sticky, td.row1announce, td.row1sticky, td.row3, td.row1, th.thcornerr, th.thright {
	background: transparent;
}
}

@-moz-document url-prefix("http://grouple.ru/private/bookmarks") {
.table {
	background: #fff;
    border: solid 1px #e0e0e0;
    border-radius: 3px;
}

.no-user-select {
	border: none;
    padding: 5px 15px;
    background: transparent;
}

.table tr, .table td, .table th {
	border: none;
    background: transparent;
}

.table > thead > tr > th {
	border: none;
    padding-top: 10px;
    padding-bottom: 10px;
    color: #fff;
	background: #34495e;
}

.table > thead > tr > th a {
	color: #fff;
    margin-left: 20px;
    border-bottom: dashed 1px transparent;
}

.table > thead > tr > th a:hover {
	color: #fff;
    border-color: #fff;
}

.table > tbody > tr {
	background: transparent;
    border-color: #f5f5f5;
    transition: ease background .22s;
}

.table > tbody > tr:hover {
	background: #fafafa;
}

.go-to-chapter {
	float: right;
    border: solid 1px rgba(0,0,0,.1);
    padding-left: 30px;
    line-height: 1;
    border-radius: 3px;
    color: #000;
    padding-right: 5px;
    transition: ease all .22s;
}

.go-to-chapter:hover {
	background-color: rgba(0,0,0,.6);
    border-color: #000;
    color: #fff;
}

.go-to-chapter:visited {
	color: #000;
}

.go-to-chapter:visited:hover {
	color: #fff;
}

.own-menu {
	background-color: #fff;
    text-align: center;
    padding: 5px;
    padding-bottom: 20px;
    color: #fff;
    opacity: .3;
    transition: ease opacity .33s;
    border-radius: 3px;
    border: solid 1px #e0e0e0;
}

.own-menu:hover {
	opacity: 1;
}


.own-menu p {
	text-align: center;
}

.own-menu a {
	display: block;
    padding: 7px 10px;
    background-color: #3498db;
    border-radius: 3px;
    border: solid 1px #2980b9;
    color: #fff;
    margin: 0 10px;
    transition: ease-in-out all .33s;
}

.own-menu a:hover {
	background: #34495e;
    border-color: #2c3e50;
    color: #fff;
}
}
