css:
@-moz-document domain("mail.google.com") {

/*move body up to hide bloat*/
body{
    margin-top:-130px;
}

/*bigger font size*/
td{
    font-size: 100%;
}

/*hide inbox options*/
input[value="Archive"] {
    display: none;
}
input[value="Report Spam"] {
    display: none;
}
input[value="Delete"] {
    display: none;
}
input[value="Go"] {
    display: none;
}
select[name="tact"]{
    display: none;
}
a[class="nu"]{
    display:none;
}

/*hide links in left column*/
a[href="?&s=r"] {
    display: none;
}
a[href="?&s=d"] {
    display: none;
}
a[href="?&s=a"] {
    display: none;
}
a[href="?&s=m"] {
    display: none;
}
a[href="?&s=t"] {
    display: none;
}
a[href="?&v=cl"] {
    display: none;
}

/*hide labels*/
.lb{
    display:none;
}

/*hide leftover border*/
.gbh{
    display: none;
}

/*larger Compose Mail*/
a[accesskey="c"] {
    font-size:130%;
}

/*hide message actions*/
a[accesskey="a"] {
    display:none;
}
a[accesskey="f"] {
    display:none;
}
a[href$="pt"] {
    display:none;
}
a[href$="dm"] {
    display:none;
}
a[href$="om"] {
    display:none;
}

/*larger Reply*/
a[accesskey="r"] {
    font-size:200%;
}

/*hide Quick Reply options*/
input[value="More Reply Options"]{
    display:none;
}
input[value="Save Draft"]{
    display:none;
}
#haot{
    display:none;
}
label[for="haot"]{
    display:none;
}
input[value="Send"]{
    font-size: 150%;
    font-weight: 700;
}
select[name="bact"]{
    display: none;
}

/*hide reply options*/
b[id="l-cc"]{
    display:none;
}
b[id="l-bcc"]{
    display:none;
}
b[id="l-s"]{
    display:none;
}
input[id="cc"]{
    display:none;
}
input[id="bcc"]{
    display:none;
}
input[class="i"]{
    display:none;
}
input[type="file"]{
    display:none;
}
input[value="Attach More Files"]{
    display:none;
}
input[value="Discard"]{
    display:none;
}
/*move textarea up to hide "Attachment" text */
textarea[class="mi"]{
    margin-top:-70px;
}


}
