    border-right: 1px solid #0003FF;
    padding: 5px 10px;
}
  .qtyTextBox {
	background: #000 none repeat scroll 0% 0%;
	border: 1px solid #DDD;
	box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.8), 0px 1px 2px rgba(0, 0, 0, 0.06) inset;
	border-radius: 3px;
	margin: 0px;
	font-size: 0.857em;
	padding: 0.2em;
	text-align: left;
	color: #fff !important;
   
}
  #success-order-summary .mbg-section {
	border-top: 1px solid #0600FF;
	background-color: #000;
}
  .innerContent {
	background-color: #000;
	box-shadow: 4px 4px 3px #E6E6E6;
	width: 100%;
	float: left;
	background-clip: padding-box;
	border: 1px solid #DDD;
	border-radius: 3px;
	outline-style: none;
	overflow: hidden;
	padding-bottom: 10px;
}
  .refundSummaryCompStyle {
	width: 100%;
	float: left;
	background-color: #000;
	padding-top: 10px;
	padding-left: 40px;
	display: block;
}
  .mainBodyBlock {
	background-color: #000;
	font-size: 16px;
	width: 100%;
	height: 100%;
	float: left;
}
  #gh-bt {
	border: 5px solid #00ccff;
}
  .outerwrapper {
	background: #121212 none repeat scroll 0px 0px padding-box;
	border: 2px none;
	border-radius: 5px;
	box-shadow: 0px 4px 12px 1px rgba(0, 0, 0, 0.6);
	height: 400px;
	margin: 0px auto;
	padding-top: 0px;
	text-align: center;
	overflow: hidden;
	position: relative;
}
  .alert-warning {
	color: #8A6D3B;
	background-color: #00072F;
	border-color: #0fe;
}
 .module, #cart-summary-ctr {
	background: #000 none repeat scroll 0% 0% padding-box;
	border: 1px solid #DDD;
	border-radius: 3px;
}
  #purchase-history .my-container.item-list .my-row-r {
    border-bottom: 1px solid #0003FF;
}
#nav-bar .nav-bar-inner .carousel .viewport {
    width: 892px;
    background: #000 none repeat scroll 0% 0%!important;
}
  #nav-bar .nav-bar-inner .nav-title-wrap {
    display: inline-block;
    float: left;
    font-size: 22px;
    padding: 9px 3px 5px 7px;
    background: #000 none repeat scroll 0% 0%;
    width: 210px;
    text-align: center;
}
  element {
	background: transparent 
}
  .my-container .no-rows {
    background-color: #000;
    padding: 20px;
    font-family: "Helvetica Neue",Helvetica,Verdana,sans-serif;
