    padding: 0px;}
    #purchase-history .bulk-actions-border {
    background: transparent none repeat scroll 0px center;
    border-bottom: 1px solid #0015FF;
}
    #icon-legend {
    border-width: 1px;
    border-style: solid;
    border-color: #0fe;
    padding: 6px;
}
    .my-ebay .menu-head {
    cursor: pointer;
    position: relative;
    font-weight: bold;
    line-height: 1.1em;
    padding: 7px 0px;
    border-top: 0px none;
    border-bottom: 1px solid #0015FF;
    margin-right: 3px;
}
    .my-ebay .menu-body ul li.wflyout:hover {
    border-width: 1px 0px 1px 1px;
    border-style: solid none solid solid;
    border-color: #0fe ;
    -border-top-colors: none;
    -border-right-colors: none;
    -border-bottom-colors: none;
    -border-left-colors: none;
    border-image: none;
    background-color: #0F00FF;
    position: relative;
}
  
  
  
  
  
  
  
    
  
  
  
