css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("9gag.com") {

/* left sidebar*/
/* + top position */
#container > .drawer{position: absolute}
/* + hide all but first sector */
.drawer > .drawer-container > section:nth-child(n+2){display:none!important;}


/* page */
/* + hide category */
section > div > div > article > header:nth-child(1) > div:nth-child(1) {display:none!important;}
/* + hide afterbar */
.post-afterbar-a {display:none!important;}
/* + hide counter */
.post-tag,.post-meta,.post-afterbar-a.in-list-view,.fixed-wrap-post-bar,post-text-container,.post-tag,div > article:nth-of-type(3){display:none !important}
/* + height fix */
#comment, #comments {margin-top: 0px;height: 0px;}


/* right sidebar */
/* + hide text */
#sidebar-content > div > .sticky > div,h4 {display:none!important;background-color: black;}
/* + top position */
#sidebar-content > div {min-height: 4000px !important;}
#sidebar-content > div > .sticky {position: absolute!important}

}
