css:
@-moz-document
/*regexp("(https|http)://(www|calendar|contacts|docs|hangouts|mail|myaccount|news|play|plus|translate|voice|wallet|pay|notifications).google.(com|([a-z]{2}))(.[a-z]{2})?/(?!calendar|contacts|docs|hangouts|mail|myaccount|news|play|plus|translate|voice|wallet|pay|notifications).*")*/
domain("google.com")
{



:root{
	--gbar-main-background-color: #111111;
	--gbar-light-background-color: #333333;
	--gbar-button-color: #222222;
	--gbar-button-color-2: #777777;
	--gbar-border-color: #555555;
	--gbar-border-color-2: #999999;
 	--gbar-accent-color: #CCCCCC;
	--gbar-text-color: #FFFFFF;
	--gbar-box-shadow: 0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23); /*creates a shadow on the bottom and right, comment out this line to remove*/
	--gbar-box-shadow2: 0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23), 0 -10px 20px rgba(0,0,0,0.19), 0 -6px 6px rgba(0,0,0,0.23); /*creates a shadow on all sides, comment out this line to remove*/
}
}

@-moz-document
/*[[GCalendar]]*/
/*[[GContacts]]*/
/*[[GDocs]]*/
/*[[GDrive]]*/
/*[[GHangouts]]*/
/*[[GMail]]*/
/*[[GMyAccount]]*/
/*[[GNews]]*/
/*[[GPlay]]*/
/*[[GPlus]]*/
/*[[GTranslate]]*/
/*[[GVoice]]*/
/*[[GWallet]]*/
/*[[GNotif]]*/
url-prefix("http://example.com"){

/*========================Google Bar at the top========================*/
/*Reminder to Vote above the Google Bar*/
/*.gb_Bc{background: var(--main-background-color) !important;}*/

/*Text color for the "Reminder to Vote" popup above the Google Bar*/
/*.gb_Fc{color: var(--text-color) !important;}
.gb_Hc{color: var(--accent-color) !important;}*/

/*Background color for the Google bar*/
header#gb /*New Google Bar*/
{
    background: var(--gbar-main-background-color) !important;
}

/*Background color for Search box*/
#gbqfqw, .gb_Se{
    /*background: var(--gbar-light-background-color) !important;*/
    background: none !important;
    background: rgba(255,255,255,0.16) !important;
    border: none !important;
}
.gb_lf{
    box-shadow: var(--gbar-box-shadow2) !important;
}

/*Text color for Search box*/
.gbqfif, .gb_df, .gb_df::placeholder{
    color: var(--gbar-accent-color) !important;
}

/*Background color for Search box popup*/
.gssb_m, .gssb_e, #\:0, #nngdp31, #nngdp12{
    background: var(--gbar-light-background-color) !important;
    box-shadow: var(--gbar-box-shadow) !important;
}
.oKubKe[aria-selected="true"], .VOEIyf .jBmls{
    background: var(--gbar-main-background-color) !important;
}

/*Text color for Search box popup*/
.cTa, .WSa, .vqNupe-MZArnb, .gssb_m, .md_subhead, gv-omnibar-contact, gv-annotation.md-subhead, .gvPageRoot .grey-900, .uCK4Db, .v4vX6d, .KfXsid{
    color: var(--gbar-button-color-2) !important;
}


/*[[IconColor]]*/



/*[[AppsPopup]]*/
}

/*=====================FIX FOR Old Google Bar Apps Icon=====================*/
@-moz-document
/*Insert urls for Calendar if needed*/
/*[[GContactsOld]]*/
/*[[GPlay]]*/
/*[[GVoiceOld]]*/
url-prefix("https://example.com"){

/*Background color for the Google bar*/
.gb_Tf{
    background: var(--gbar-main-background-color) !important;
}

/*Search box drop down text color*/
.hNW13c{
    color: var(--gbar-text-color) !important;
}

/*Apps icon in Google bar*/
.gb_B.gb_pc{
    border: none !important;
    /*background-image: url('https://ssl.gstatic.com/gb/images/v1_76783e20.png') !important;*/
    background-position: -0px -138px !important;
    opacity: 1.00 !important;
}

/*Fix for Google Play logo*/
.gb_la{
    display: none !important;    
}
.gb_ke{
    width: 183px !important;
    height: 39px !important;
    /*https://i.imgur.com/QgS6N1E.png*/
    background: url(data:image/png;base64,) !important;
    background-size: 183px 39px !important;
}
}

/*=====================FIX FOR Google Plus+=====================*/
@-moz-document
/*[[GPlus]]*/
url-prefix("https://example.com"){

/*Apps icon in Google bar*/
.gb_B.gb_pc{
    border: none !important;
    /*background-image: url('https://ssl.gstatic.com/gb/images/v1_76783e20.png') !important;*/
    background-position: -0px -138px !important;
    opacity: 1.00 !important;
}

/*Background color*/
.XS1fT{
    background: var(--gbar-main-background-color) !important;
}

/*Main background color*/
.DAbEod{
    background: var(--gbar-main-background-color) !important;
}

/*Text Color*/
.wTeX3, .X5OP5b, .gDeJqe, .GIqPVe, .dFghye{
    color: var(--gbar-accent-color) !important;
}

/*Google Plus logo on left side*/
.tZshNe{
    /*background-image: url('https://ssl.gstatic.com/images/branding/lockups/2x/lockup_gplus_light_color_88x24dp.png') !important;*/
    background-image: url(data:image/png;base64,) !important;
}
}
