css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.ivlog.tv") {

    body {
font-family: Arial, sans-serif;
margin:    10px;
background-image:url(http://www.socaleventvideos.com/wp-content/uploads/2014/12/black-background.jpg);
    }
.header{
width:      unset;
height: 40px;
}

.display{   
     width:              initial;
 
}
.headcontainer {
    width:        99%;
    margin-left:           0px;
    margin-right:       0px;
}
    
    .mainbody {
    background-position: initial;
    width:   92%;
    display:           -moz-stack;
}
    
    
.page{
background-image:url(http://www.socaleventvideos.com/wp-content/uploads/2014/12/black-background.jpg);
background-position:center;
background-repeat: repeat;
background-position:top;
}



.cam {
  background-color : transparent;
    float:                  inherit;
    margin-right: 1px;
    margin-left: 1px;
    border-radius: 1px;
    margin-top: 1px;
    width:   250px;
    height:     140px;
}
.proCams{
background-color :    black;
width:   1280px;
border-radius: 5px;
box-shadow: 1px 1px 1px ;
margin-bottom:      inherit;
position:         initial;
left: 1px;
}
    
    .banner{
/* User Banner */
width:  -moz-min-content;
height:  -moz-min-content;
background-position: initial;
background-repeat:no-repeat;
}
    
    #chat {
         width:     700px;
   
    overflow:         visible;
    word-wrap:            normal;
    padding-top: 3px;
    font-size: 12px;

}
    
#chatWrapper {
    box-shadow: 2px 2px 2px #828181 inset; 
       float:         right;
    
    width:     700px;
    background-color:rgba(255,255,255,1);
    border-radius: 3px;
    
}
 #chatbuttons {
     height:   100px;
    
}
    
    .vcount {
    background-color: #444;
    border-radius: 0px 0px 10px 10px;
    text-align: center;
    width: 95px;
    font-size: 15px;
    color:    chartreuse;
    float:              none;
}

#titleholder {
    width: 700px;
    height: 24px;
    float:         right;
    background-color: #FFF;
    border-radius: 10px;
    box-shadow: 1px 4px 2px #828181 inset;
    padding-top: 9px;
    font-size: 14px;
    overflow:    -moz-hidden-unscrollable;
}

}
