css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("songza.com") {

#know-box, #promote-box, #ad-header, #logo, #logo a, #search h2, #footer, #powered-by, .extras, #video-ad-header, #video-ad-content iframe, #currently-playing, #playlist-box h2, #play-marker img, #songza-status, .loading img, div.prWrap, #player-header, #side-ad-box  {
display: none !important;
}

body, .tabs-nav li, .tabs-container, #currently-playing .box, .ctl-button, li.tabs-selected {
background-color: #000 !important;
}

body, .reded, .genre-title {
font-family: "Segoe UI", "Century Gothic", sans-serif !important;
color: #fff !important;
}

a, a * {
color: #fff !important;
}
a[onclick], a#show-notify {
cursor: pointer !important;
}

.song {
opacity: 1 !important;
}

#frame {
padding-top: 10px !important;
max-width: 800px !important;
}
#frame, #page-wrap {
background: transparent !important;
}
#content {
margin-top: 0 !important;
width: 100% !important;
}

#login {
margin: 8px 0 0 0 !important;
padding: 0 !important;
text-align: right !important;
}

#search {
padding-top: 5px !important;
text-align: left !important;
background: transparent url(data:image/png;base64,) no-repeat 500px 15px;
}
#search .form {
margin: 0 !important;
}
input {
border: inherit !important;
background: inherit !important;
position: static !important;
}
input#query {
width: 48% !important;
}
#submit {
height: auto !important;
position: static !important;
padding: 0 5px !important;
}

.tabs-nav li a {
color: #aaa !important;
}
.tabs-nav li.tabs-selected a {
color: #fff !important;
}
li.tabs-selected {
-moz-border-radius: 6px 6px 0 0 !important;
}

.box {
border: none !important;
}

#playlist-message, #change-message, #recommended h3, a#results-more {
background-color: #333!important;
border-width: 0 !important;
color: #fff !important;
-moz-border-radius: 5px !important;
}
ul.playlist-actions li a, a#show-notify {
-moz-border-radius: 5px !important;
}
a#results-more {
cursor: pointer !important;

}

#left-col, #right-col {
width: 48% !important;
margin: 0 !important;
}
#right-col {
/* to allow scrolling in case the video is overlapping */
padding-bottom: 240px !important; 
}
#left-col {
padding-bottom: 40px !important;
}
#playlist-col,  #misc-col {
width: 100% !important;
}
#playlist li .song-title {
width: 94% !important;
}

#misc-col {
height: 0 !important;
}
#video-ad-box {
height: auto !important;
}
#player-content {
text-align: center !important;
position: fixed !important;
right: 10px !important;
bottom: 40px !important;
-moz-border-radius: 8px !important;
}

#play-marker {
background-color: #f60 !important;
display: block !important;
width: 12px !important;
height: 12px !important;
margin-left: -17px !important;
margin-top: 5px !important;
-moz-border-radius: 6px !important;
}

#player-wrap {
height: 30px !important;
overflow: visible !important;
border: 3px solid #000 !important;
}

#player-ctls {
position: absolute !important;
padding: 0 !important;
margin: 0 !important;
bottom: 0 !important;
left: 0 !important;
width: 37px !important;
height: 26px !important;
border-width: 2px 1px 2px 2px !important;
border-style: solid !important;
border-color: #fff !important;
}
#player-ctls, #player-ctls .ctl-button {
-moz-border-radius: 9px 0 0 9px !important;
}

#player-ctls .ctl-button {
position: absolute !important;
left: 0 !important;
visibility: visible !important;
border-style: solid !important;
border-color: #000 !important;
border-width: 5px 9px 5px 7px !important;
margin: 0 !important;
width: 20px !important;
height: 16px !important;
cursor: pointer !important;
}
#player-ctls .ctl-button a {
display: block;
background: transparent !important;
position: absolute !important;
width: 40px !important;
height: 40px !important;
top: -12px !important;
left: -10px !important;
}
#player-ctls .ctl-button-active {
visibility: hidden !important;
}
.ctl-section {
border: none !important;
}
#player-center {
margin-left: 40px !important;
margin-right: 146px !important;
}
#player-left {
position: absolute !important;
left: 45px !important;
margin: 0 !important;
padding: 0 !important;
}
#player-left, #songza-name {
width: auto !important;
color: #eee !important;
}
#ctl-progress {
margin: 0 !important;
}
#ctl-progress, #ctl-progress-played, #ctl-progress-loaded {
height: 30px !important;
}
#ctl-progress-loaded {
top: -30px !important;
opacity: 1 !important;
background-color: rgba(90, 0, 0, 0.25) !important;
}
#player-right {
border-left: 1px solid #222 !important;
}

.loading {
margin: 20px auto !important;
width: 48px !important;
height: 48px !important;
background: transparent url('data:image/gif;base64,') !important;
}

}
