/*This style replaces the new red heart icon on Twitter with the old yellow fav star.
Version 1.1 by simodk @ userstyles.org*/ 

@-moz-document domain(twitter.com) {

.HeartAnimationContainer {
  visibility: hidden;
}

.HeartAnimationContainer:after {
  content: '?';
  visibility: visible;
  display: block;
  position: absolute;
}

  
/* outdated code, here just for reference
  .ProfileTweet-action--favorite.withHeartIcon .ProfileTweet-actionButton:hover,.ProfileTweet-action--favorite.withHeartIcon .ProfileTweet-actionButton:focus,.ProfileTweet-action--favorite.withHeartIcon .ProfileTweet-actionCount:hover,.ProfileTweet-action--favorite.withHeartIcon .ProfileTweet-actionCount:focus,.favorited .ProfileTweet-action--favorite .Icon--heart,.favorited .ProfileTweet-action--favorite.withHeartIcon .ProfileTweet-actionButtonUndo {
	color:#ffac33;
}*/
  
  
.ProfileTweet-action--favorite .ProfileTweet-actionButton:hover, .ProfileTweet-action--favorite .ProfileTweet-actionButton:focus, .ProfileTweet-action--favorite .ProfileTweet-actionCount:hover, .ProfileTweet-action--favorite .ProfileTweet-actionCount:focus, .favorited .ProfileTweet-action--favorite .Icon--heart, .favorited .ProfileTweet-action--favorite .ProfileTweet-actionButtonUndo {
  color:#ffac33
}
  
}
