css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iichan.hk") {
.adminname {
	color: #00FFAC;
}
  .greeting {
	text-align: center;
	font-weight: bold;
	color: #00FFAC;
}
  .news {
	background: #37CA9A;
	color: #444;
	padding: 0px 3px 0px 3px;
	font-weight: bold;
	text-align: center;
	margin-bottom: 5px;
}
  .list div a:hover {
	background: #88DFC3;
}
  .logo a {
	font-size:24pt;
	color:#AF0A0F;
	font-family:serif;
}

.category {
	font-family: sans-serif;
	font-size: small;
	margin-bottom: 8px;
	background: #2C333D;
}

.header {
	background: #9988EE;
	color: #0F0C5D;
	padding: 0px 3px 0px 3px;
	font-weight: bold;
}

.list {
	border-left: 0px solid #D6DAF0;
	border-right: 0px solid #D6DAF0;
	border-bottom: 0px solid #D6DAF0;
}
  .header {
	background: #37CA9A;
	color: #444;
	padding: 0px 3px 0px 3px;
	font-weight: bold;
}
  .banner {
	margin-top: -30px;
	margin-bottom: 5px;
}
html,body {
	background-color:#2C333D;
	color:#C7C7C7;
	font-family:"Roboto","Open Sans","Trebuchet MS",Trebuchet,sans-serif;
	font-size:15px;
}
a {
	color:#37CA9A;
    text-decoration: none;
}
a:visited {
	color:#37CA9A;
    text-decoration: none;
}
a:hover {
	color:#43826D;
    text-decoration: none;
}
.adminbar {
	clear:both;
	float:right;
	font-size:.8em;
}
.adminbar a {
	font-weight:bold;
}
.logo {
	clear:both;
	text-align:left;
	font-size:2em;
	font-weight:bold;
	color:#37CA9A;
    text-shadow: 0 0 2px #888;
    margin-left: 5px;
}
.replymode,.passvalid,.catalogmode {
	background:rgba(255,255,255,0.13);
	text-align:center;
	padding:2px;
	color:#FFFFFF;
	clear:both;
	font-weight:bold;
	margin-bottom:.5em;
	border-radius:5px;
}
.postarea {
}
.postblock {
	background:transparent;
	color:#999;
	font-family:Roboto,sans-serif;
	text-shadow:0 0 2px #999;
	font-weight:bold;
}
.footer {
	text-align:center;
	font-size:12px;
	font-family:serif;
	margin:2em 0 0 0;
}
.dellist {
	font-weight:bold;
	text-align:center;
}
.delbuttons {
	text-align:center;
	padding-bottom:4px;
}
.managehead {
	background:#DDDDDD;
	color:#002244;
	padding:0px;
}
.postlists {
	background:#FFFFFF;
	width:100%;
	padding:0px;
	color:#800000;
}
.row1 {
	background:#DDDDDD;
	color:#002244;
}
.row2 {
	background:#CCCCCC;
	color:#002244;
}
.unkfunc {
	background:inherit;
	color:#789922;
}
.reflink {
	font-size:.8em;
	font-weight:bold;
	padding-right:6px;
}
.reflink a,.reflink a:visited {
	color:rgb(180,180,180);
}
.reflink a:hover {
	color:rgb(230,230,230);
}
.filesize {
	text-decoration:none;
	color:#999;
	font-family:Roboto,sans-serif;
	font-weight:100;
	text-shadow:0 0 2px #999;
	font-size:.8em;
}
.filetitle {
	background:inherit;
	font-size:1.2em;
	color:#8DA1B4;
	font-weight:bold;
}
.postername {
	color:#789EB8;
	font-weight:bold;
}
.postertrip {
	color:#FF3300;
}
.oldpost {
	color:#CC1105;
	font-weight:bold;
}
.omittedposts,.omittedposts a,.xlink {
	color:rgba(255,255,255,0.47)!important;
}
.reply,.highlight {
	background:#363F4C;
	padding-right:3px;
	padding:0 3px 1px 1px;
	border:1px solid rgb(31,37,44);
}
blockquote {
	margin:.5em .5em .5em 1em;
}
blockquote p {
	margin:0;
	padding:0;
}
.reply blockquote {
	margin:.5em;
}
.doubledash {
	display:none;
	vertical-align:top;
	clear:both;
	float:left;
}
.replytitle {
	font-size:1.2em;
	color:#002244;
	font-weight:bold;
}
.commentpostername {
	color:#004A99;
	font-weight:800;
}
.thumbnailmsg {
	font-size:.8em;
	color:#666666;
}
hr {
	border-style:solid none none none;
	border-width:1px;
	border-color:rgba(0,0,0,0.27);
	box-shadow:0 -1px 0 rgba(0,0,0,0.27);
}
table {
	border-style:none;
}
table td {
	border-style:none;
}
.nothumb {
	background-color:transparent;
	border-style:dotted;
	margin:.3em .5em;
}
.abbrev {
	color:#666666;
}
.highlight {
	border:1px solid rgba(85,224,156,0.28);
	box-shadow:inset 0 0 59px rgba(85,224,156,0.28);
}
#watchedthreads {
	background-color:#DDDDDD!important;
	border:1px solid #CCCCCC!important;
	border-radius:5px;
}
.spritebtn {
	border-color:#aba8a6;
	opacity:0.7;
}
.spritebtn:hover {
	background-color:rgba(55,201,153,0.67);
	opacity:1;
}
.topbar-left {
	height:0px;
}
.topbar-right {
	height:0px;
}
.topbar-center {
	height:6px;
	background:rgba(0,0,0,0.06);
}
.content-background {
	background-color:#2C333D;
}
.maintable {
	width:100%;
	table-layout:fixed;
}
#overlay_menu {
	-webkit-transition-property:background-color,color,box-shadow;
	-webkit-transition-duration:0.4s;
	-o-transition-property:background-color,color,box-shadow;
	-o-transition-duration:0.4s;
	-moz-transition-property:background-color,color,box-shadow;
	-moz-transition-duration:0.4s;
	transition-property:background-color,color,box-shadow;
	transition-duration:0.4s;
	box-shadow:none;
	border-color:rgb(31,37,44);
}
#overlay_menu:hover {
	box-shadow:0 0 20px rgba(0,0,0,1);
	background:rgb(29,34,40);
}
.replies table {
	border-spacing:2px 4px;
}
.menu-item {
	padding:2px 5px;
	border-radius:3px;
}
.maintable hr {
	margin-left:-9px;
	margin-right:-9px;
}
.reflinkpreview {
	border:none;
	border-radius:0;
	box-shadow:0 0 20px #000000;
}
input[type="text"],input[type="password"],textarea {
	border:1px solid rgba(255,255,255,0.18);
	background:rgb(42,48,55);
	-webkit-transition-property:background-color,border-color,box-shadow;
	-webkit-transition-duration:0.4s;
	transition-property:background-color,border-color,box-shadow;
	-o-transition-duration:0.4s;
	-o-transition-property:background-color,border-color,box-shadow;
	-moz-transition-duration:0.4s;
	-moz-transition-property:background-color,border-color,box-shadow;
	transition-duration:0.4s;
	color:rgb(167,167,167);
}
input[type="text"]:hover,input[type="password"]:hover,textarea:hover {
	background:rgb(29,34,40);
}
input[type="text"]:focus,input[type="password"]:focus,textarea:focus {
	border:1px solid rgba(55,201,153,0.62);
	background:rgb(29,34,40);
	outline:none;
	box-shadow:inset 0 0 40px rgba(55,201,153,0.32);
	color:rgb(216,216,216);
}
.reflinkpreview .reply {
	-webkit-box-shadow:none;
	-moz-box-shadow:none;
	box-shadow:none;
}
.prettyprint {
	overflow:auto;
	max-height:500px;
	text-shadow:none;
	color:black;
	background:rgba(255,255,255,0.72);
}
.prettyprint-container {
	display:inline-block;
	margin-left:2px;
	margin-bottom:4px;
	margin-top:4px;
}
.omittedposts a,.xlink {
	text-decoration:inherit;
	border-bottom:1px dashed rgb(143,143,143);
}
.postnode label {
	font-size:.9em;
	color:#999;
	font-family:Roboto,sans-serif;
	font-weight:100;
	text-shadow:0 0 1px #999;
}
.uibutton {
	background:rgb(54,63,76);
	border:1px solid rgba(255,255,255,0.18);
	border-radius:4px 4px 0 0;
	border-bottom:0;
}
.uibutton:hover {
	background:rgb(77,88,103);
	border:1px solid rgb(83,83,83);
	color:#37CA9A;
}
#postclone,#postclone .captchaimage {
	background-color:rgb(29,34,40);
}
.postmessage span[style*="color:blue"] {
	color:rgb(173,212,255)!important;
}
.markupbtns {
	margin-bottom:-2px;
}
.reply {
	color:#37CA9A;
	font-family:serif;
}
.commentpostername {
	color:#fff;
	font-weight:800;
}
.spoiler {
	background: #000;
	color: #000;
}
.spoiler:hover {
	background: #000;
	color: #909090;
}}
