css:
/* Version 1.1 */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.facebook.com") {
  /* Hide these items */
  #leftCol, #rightCol, #pagelet_sidebar, ._la, ._54bz {
    display: none !important;
    width: 0px !important;
  }
  
  /* Stretch these items */
  #globalContainer, #contentArea, ._22ss {
    width: 100% !important;
    padding-right: 0px !important;
    padding-left: 0px !important;
  }
  
  /* Adjust the content column */
  #contentCol {
    padding-left: 0px !important;
    margin-left: 0px !important;
  }
  
  /* Center these items */
  .uiScaledImageContainer, ._5mly, ._4-eo, ._2a2q {
    position: relative !important;
    margin-left: auto !important;
    margin-right: auto !important;
  }
  
  /* Stretch and center the content column */
  #content {
    width: /*[[wid]]*/%;
    margin: auto;
  }
}
