css:
@-moz-document domain("web.tweetdeck.com") {
/* Nudge userstyles.org into categorizing the userstyle correctly. */
}
@-moz-document domain("tweetdeck.twitter.com") {
/* Activity type icons: */
.activity-indicator.icon-favorite-color {
	color: transparent !important;
    background:  url('data:image/png;base64,') center no-repeat;

}

/* Corner icons: */
.is-favorite .tweet-dogear {
	background-image: url('data:image/png;base64,');
    background-position: 0 0;
}
.is-retweet.is-favorite .tweet-dogear {
	background-image: url('data:image/png;base64,');
	background-position: 0 0;
}

/* Favorite button base: */
.icon-favorite-toggle {
	color: transparent !important;
    background-position: center;
    background-repeat: no-repeat;
}

/*[[theme]]*/

/* Favorite button set: */
.is-favorite .icon-favorite-toggle {
    background-image:  url('data:image/png;base64,');
}
}
