css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kickass.to"), domain("kat.cr"), domain("kickassto.co"), domain("katproxy.is"), domain("thekat.tv"), domain("kickass.so"), domain("kickasstor.eu"), domain("kickasstor.bitproxy.eu"), domain("kickassto.come.in"), domain("kasssto.come.in"), domain("kickassunblock.info"), domain("kickasstorrents.website"), domain("kickasstorrents.to"), domain("kickasstorrents.ee"), domain("katcr.to"), domain("kickasstorrents.pw") 
{

	/* Remove sidebar, footbar and side buttons */
	#feedback, .lightgrey, #showsidebar, .sidebarCell, .torrentfingers, a.chatButton {
		display: none !important;
	}
	
	/* Background */
	.tagcloud, html, .land-content, .lightivorybg, .lightivorybg:hover, .pollresultsblock {
		background: #333030 !important;
	}
	
	/* Tags Cloud collpes. Header, footer and search in front page */
	.showmore, .landing-header, .land-login, .bottom-menu-block, .land-search-input {
		background: #212121 !important;
	}
	
	
	/**************************/
	/* Tables and search page */
	/**************************/
	
	/* Table coloring */
	.doublecelltable {
		max-width: 1280px !important;
		margin: auto !important;
	}
	.firstr, .forumGroupName {
		background: #424242 !important;
	}
	.odd {
		background: #212121 !important;
	}
	.even {
		background: none !important;
	}
	.odd:hover, .even:hover {
		background: #666 !important;
	}
	
	/* Torrent info text color (Size and number of files) */
	td {
		color: #BBB !important;
	}
	
	/* Buttons and tabs */
	.iaconbox > a > .ka, .iaconbox > .kaButton, .smallButton, .kasmall, .kaGiantButton, .siteButton, .darkButton, #fancybox-close .ka-delete {
		background: #594C2D !important;
		color: #EEE !important;
		transition-duration: .4s !important;
	}
	.iaconbox > a > .ka:hover , .iaconbox > .kaButton > .ka-comment:hover, .smallButton:hover, .kasmall:hover, .kaGiantButton:hover, .siteButton:hover, .darkButton.selectedTab, #fancybox-close .ka-delete:hover {
		background: #8C7747 !important;
	}
  
	.iaconbox > a > .ka-comment {
		background: none !important;
	}
	
	/*Green crown*/
	a[title="Verified Torrent"] .ka-green, a[title="Verified Torrent"] .ka-green::before  {
		background: #33572B !important;
		transition-duration: .4s !important;
	}
	a[title="Verified Torrent"] .ka-green:hover, a[title="Verified Torrent"] .ka-green:hover::before {
		background: #79971D !important;
	}
	
	/* Orenge dislike and report buttons (like button is in the long list) */
	.ka-red {
		background: #6A4036 !important;
		transition-duration: .5s !important;
	}
	.ka-red:hover {
		background: #9D5F50 !important;
	}
	
	/* Pages Buttons */
	.turnoverButton {
		background: #666 !important;
		color: #EEE !important;
	}
	.turnoverButton:hover {
		background: #888 !important;
	}
		
	/****************/
	/* Torrent page */
	/****************/
	
	/* Like and Dislike for torrent */
	.thnx, .fake {
		background: none !important;
		color: #EEE !important;
	}
	.thnx:before, .fake:before {	
		font-size: 52px !important;
		border-radius: 2px !important;
		transition-duration: .5s !important;
	}
	.thnx:before {
		background: #008F0D !important;
	content: "?";
	}
	.fake:before {
		background: #B94309 !important;
		content: "?";
	}
	.thnx:hover:before, .fake:hover:before {
		background: #594C2D !important;
	}
	
	.ka-verify:before { /* Crown in torrent page */
		background: #594C2D !important;
		box-shadow: none !important;
	}
	
	/* Summery box */
	.accentbox {
		background: #BBB !important;
		color: #212121 !important;
		border-radius: 2px !important;
	}
	
	/* Torrent files tree */
	.torrent_files * {
		background-color: #333030 !important;
	}
	
	/*****************/
	/* Comments page */
	/*****************/
	
	/* Comment */
	.commentbody {
		background: #212121 !important;
	}
	
	.qrate {
		background: #424242 !important;
	}
	
	/*************/
	/* Searching */
	/*************/
	
		/* Advanced search */
	#fancybox-content {
		background: #333030 !important;
	}
	.formtable select, .formtable input {
		background: #666 !important;
		color: #DDD !important;
		border: none !important;
	}
	
	/* Search autocomplete box */
	.ui-corner-all{
		background: #212121 !important;
		border: none !important;
	}
	.ui-corner-all:hover{	
		background: #333 !important;
	}
	
	/**********/
	/* Others */
	/**********/
	
	/* Preview popup */
	#prevAV {
		background: #594C2D !important;
	}
	.tail { /* Right arrow */
		background: none !important;
		width: 0 !important;
		height: 0 !important;
		border-top: 8px solid transparent !important;
	  border-bottom: 8px solid transparent !important;
		border-left: 8px solid #594C2D !important;
	}
	#previewPopupContent {
		background: #212121 !important;
		color: #BBB !important;
		-webkit-box-shadow: 3px 3px 8px 0px rgba(0,0,0,0.7) !important;
    box-shadow: 3px 5px 8px 0px rgba(0,0,0,0.7) !important;
	}
	
		/* Remove ALL border and shadows */
	* {
		border: none !important;
		box-shadow: none !important;
		text-shadow: none !important;
	}
	
	/* Links without underline and stronger color */
	a, h1 a, .numvotes {
		color: #C5C5C5 !important;
	}
	a:hover {
		text-decoration: none !important;
		color: #EEE !important;
	}
}
