css:
@-moz-document url-prefix(https://twitter.com) {
/* TL用 */
.HeartAnimation {
    background-image: url(data:image/png;base64,) !important;
}

/* 赤色を黄色に */
.ProfileTweet-action--favorite.withHeartIcon .ProfileTweet-actionButton:hover,
.ProfileTweet-action--favorite.withHeartIcon .ProfileTweet-actionButton:focus,
.ProfileTweet-action--favorite.withHeartIcon .ProfileTweet-actionCount:hover,
.ProfileTweet-action--favorite.withHeartIcon .ProfileTweet-actionCount:focus,
.favorited .ProfileTweet-action--favorite .Icon--heart,
.favorited .ProfileTweet-action--favorite.withHeartIcon .ProfileTweet-actionButtonUndo,
.WebToast.favorited .Icon--heart,
.WebToast-action--heart:hover,
.WebToast-action--heart:focus,
.WebToast-action--heart:active,
.Icon--colorHeart,
.Icon--circleActiveHeart,
.Icon--heartBadge {
    color: #F8AA42 !important;
}


/* トーストのふぁぼボタン */
.WebToast-action--heart .Icon--heart:before {
    content:"\f147" !important;
}

/* 通知のアイコン */
.Icon--heartBadge:before {
    content:"\f001" !important;
}

/* テキスト置換 */
[data-original-title="いいね"] .HeartAnimation:before {
    content: "ふぁぼ";
    width: 40px;
    left: -6px;
}
.ProfileNav-item--favorites .ProfileNav-label {
    font-size:0;
}
.ProfileNav-item--favorites .ProfileNav-label:before {
    content:"ふぁぼ";
    font-size:11px;
    visibility: visible;
}
[data-original-title="「いいね」を取り消す"] .HeartAnimation:before {
    content: "「ふぁぼ」を取り消す";
    width: 140px;
    left: -55px;
}
[data-activity-popup-title*="いいね"]{
    visibility: hidden;
}
[data-activity-popup-title*="いいね"]:after {
    content:"ふぁぼ";
    position:relative;
    left:-3.25em;
	visibility: visible;
}
[data-activity-popup-title*="いいね"] strong{
	visibility: visible;
}


/* ツールチップを再作成 */
.HeartAnimation:before {
    overflow: hidden;
    visibility: visible;
    position: absolute;
    top: -26px;
    display: block;
    max-width: 180px;
    padding: 10px;
    color: #fff;
    font-size: 12px;
    text-align: center;
    text-decoration: none;
    text-overflow: ellipsis;
    background-color: #292f33;
    z-index: 5002;
    opacity: 0;
    -ms-filter: "alpha(opacity=0)";
    white-space: normal;
    transition: opacity .3s ease;
    -webkit-transform: translateZ(0px);
    transform: translateZ(0px);
    border-radius: 4px;
}
.HeartAnimation:hover:before,
.HeartAnimation:hover:after {
    opacity: .95;
    -ms-filter: "alpha(opacity=95)";
    transition: opacity .1s ease;
}
.HeartAnimation:after {
    content: "";
    position: absolute;
    top: 5px;
    left: 50%;
    width: 0;
    height: 0;
    margin-left: -5px;
    opacity: 0;
    border-top: 5px solid #292f33;
    border-right: 5px solid transparent;
    border-left: 5px solid transparent;
    transition: opacity .3s ease;
}
[data-nav="favorites"] .ProfileNav-label {
    position: relative;
    visibility: hidden;
}
[data-nav="favorites"]:not(.ProfileNav-stat) .ProfileNav-label:before {
    visibility: visible;
    content: "ふぁぼ";
    position: absolute;
    width: 40px;
}

