css:
@-moz-document domain("tibiawindbot.com") {
.navbar {
    background: url(http://i.imgur.com/2jtUX1D.png) repeat-x top left;
}
.navRight {
    background: url(http://i.imgur.com/EOPE4rz.png) no-repeat top right;
}
.navLeft {
    background: url(http://i.imgur.com/mb5h5gL.png) no-repeat top left;
}
.headerLeft {
    background: url(http://i.imgur.com/bCufQdy.png) no-repeat top left;
    height: 110px;
}
.headerRight {
    background: url(http://i.imgur.com/amhYWZV.png) no-repeat top right;
}
#headerMain {
    background: url(http://i.imgur.com/niXyBQd.png) black;
    background-position: 0px 0px;
    position: relative;
}
html {
    background: #c7ccc8 url(http://i.imgur.com/i6DPalY.png);
}
.topBarRight {
    background: url(http://i.imgur.com/4VYKAUH.png)no-repeat top right;
}
.topBarLeft {
    background: url(http://i.imgur.com/vGiCChN.png) no-repeat top left;
}
#topBar {
    background: url(http://i.imgur.com/I4kUQkr.png);
}
.searchWrap {
    background: url(http://i.imgur.com/ydrVdud.png) no-repeat top left;
}
.searchBox input[type="image"] {
    height: 0 !important;
    width: 0 !important;
    /* these numbers match the new image's dimensions */
    
    padding-left: 50px !important;
    padding-top: 48px !important;
    background: url(http://i.imgur.com/qIRZBom.png) no-repeat !important;
}
.pagination span a.popupctrl {
    background: #3D3C3C url(https://forums.tibiawindbot.com/images/styles/Xbox/misc/black_downward_arrow.png) no-repeat center left
}
div.pageHeader {
    border-color: #ffffff;
}
.navtabs,
#contentMain,
.body_wrapper,
.pageRight,
.pageLeft,
#pageWrapper,
.pageRight,
.pageLeft,
#pageWrapper,
.contentBL,
.contentBR,
.bbcode_container div.bbcode_quote {
    background: #191919;
    color: #F1F1F1;
}
.navtabs li a.navtab,
.breadcrumb .navbit a:link,
.breadcrumb .navbit a:visited,
.breadcrumb .navbit.lastnavbit span {
    color: white;
}
.navtabs li.selected a.navtab {
    color: #7fa432;
}
#loginBar,
.toplinks .notifications a.popupctrl:hover,
.toplinks .nonotifications a.popupctrl:hover,
.toplinks .nonotifications a.popupctrl.active,
.toplinks .notifications a.popupctrl.active,
.toplinks .nonotifications .popupbody a,
.popupbody,
#threadlist .threadlisthead,
.threadbit .sticky,
.threadbit .nonthread,
.threadbit .nonsticky,
.threadbit .deleted,
.threadbit .discussionrow,
.threadbit .ignored,
.blockbody,
body,
body.content,
#sidebar_container .userprof_content .blockrow {
    background: #2E332F;
    color: white;
}
.toplinks ul.isuser li.welcomelink,
.toplinks ul.isuser li,
.forumrow .forumtitle a:link,
.forumrow .forumtitle a:visited,
.forumrow .lastposttitle a:link,
.forumrow .lastposttitle a:visited,
.forumdisplaypopups a.popupctrl,
.forumdisplaypopups.popupgroup .popupmenu a.popupctrl,
.postlist_popups h6 a.popupctrl,
.postlist_popups.popupgroup .popupmenu h6 a.popupctrl,
.postlist_popups h6 a.firstunread,
.postlist_popups.popupgroup .popupmenu h6 a.firstunread,
#postlist_popups a,
.postlist_popups a,
dl.stats dd,
#sidebar_container .userprof_content .time,
.userprof_content .time,
#posts .posthead a:link,
#posts .posthead a:visited,
#posts .posthead .time {
    color: #7fa432;
}
#loginBar a:link,
#loginBar a:visited,
.tcat a:link,
.tcat a:visited,
.tcat .forumtitle,
.tcat h2,
.above_threadlist .threadpagenav .threadpagestats,
.above_postlist .pagination_top .pagination,
.above_postlist .pagination_top .postpagestats,
.forumdisplaypopups a:hover.popupctrl,
.forumdisplaypopups.popupgroup .popupmenu a:hover.popupctrl,
.postlist_popups h6 a:hover.popupctrl,
.postlist_popups.popupgroup .popupmenu h6 a:hover.popupctrl,
.postlist_popups h6 a:hover.firstunread,
.postlist_popups.popupgroup .popupmenu h6 a:hover.firstunread,
#postlist_popups a:hover,
.postlist_popups a:hover,
dl.stats dt,
.postbit .posthead,
.postbitlegacy .posthead,
.eventbit .eventhead {
    color: #E0E0E0;
}
.tcatRight,
.pagination span a,
.blockfoot,
.blocksubfoot {
    border: 1px solid white;
    background: #3D3C3C
}
#sidebar_container>ul,
#sidebar_container .blocksubhead,
.formcontrols .blockrow,
.navlinks {
    background: #3D3C3C
}
#sidebar_container .blockrow,
#sidebar_container .blockbody,
.thread_info .blockbody {
    background: #3D3C3C;
    color: #E0E0E0;
}
.thead_bar .theadrow,
.forumbit_nopost .forumbit_nopost .forumrow,
.forumbit_post .forumrow,
body.iframe h5,
body.iframe p,
body.iframe blockquote,
.day-separator span {
    color: #E0E0E0;
    background: #212121;
}
body.iframe,
li.activitybit {
    border: 1px solid white;
    color: #E0E0E0;
    background: #212121;
}
.threadbit .threadinfo,
.threadbit .alt,
.threadlastpost,
.threadlisthead span.threadinfo,
.threadlisthead span.threadstats,
.forum_info .blockbody,
.postbitlegacy .postbody,
.postbitlegacy .postrow,
.eventbit .eventrow,
.postbitdeleted .postrow,
.postbitignored .postrow,
.postbitlegacy .userinfo,
.postbitlegacy .postdetails,
.userinfo_extra dd,
.user_rep dd,
#sidebar_container.member_summary {
    color: #E0E0E0;
    background: #212121;
}
#forums a.username,
.time,
.wgo_block .section,
.wgo_block .blocksubhead {
    color: darkgray;
}
.user-avatar div {
    border: 3px solid #E0E0E0;
}
.toplinks .notifications a.popupctrl {
    background: #404040 url(https://forums.tibiawindbot.com/images/styles/Xbox/misc/arrow.png) right center no-repeat;
}
.postbitlegacy .postfoot .textcontrols,
.postbit .posthead,
.postbitlegacy .posthead,
.eventbit .eventhead {
    background: #757575
}
.postbitlegacy .postfoot .textcontrols a.newreply {
    color: #E0E0E0;
    background: url(https://forums.tibiawindbot.com/images/styles/Xbox/buttons/quote_40b.png) no-repeat left
}
.postbitlegacy .postfoot .textcontrols a.quickreply {
    color: #E0E0E0;
    background: url(https://forums.tibiawindbot.com/images/styles/Xbox/buttons/reply_40b.png) no-repeat left
}
.postbitlegacy .postfoot .textcontrols a.multiquote {
    color: #E0E0E0;
    background: url(https://forums.tibiawindbot.com/images/styles/Xbox/buttons/multiquote-back_40b.png) no-repeat left
}
.postbitlegacy .postfoot .textcontrols a.report {
    color: #E0E0E0;
    background: url(https://forums.tibiawindbot.com/images/styles/Xbox/buttons/report-40b.png) no-repeat left
}
.postbitlegacy .postfoot .textcontrols a.reputation {
    color: #E0E0E0;
    background: url(https://forums.tibiawindbot.com/images/styles/Xbox/buttons/reputation-40b.png) no-repeat left
}
#sidebar_container .blocksubhead {
    color: #E0E0E0;
    background: transparent;
}
.postbitlegacy .postfoot .textcontrols a.editpost,
.postbit .postfoot .textcontrols a.editpost,
.eventbit .eventfoot .textcontrols a.editevent {
    color: #E0E0E0;
    background: url(https://forums.tibiawindbot.com/images/styles/Xbox/buttons/edit_40b.png) no-repeat left
}
.cke_skin_kama .cke_wrapper.cke_ltr,
.cke_skin_kama .cke_wrapper.cke_rtl {
    background: #757575 url(https://forums.tibiawindbot.com/clientscript/ckeditor/skins/kama/images/sprites.png) repeat-x 0px -1950px
}
}
