css:
@-moz-document url-prefix('https://tree.taiga.io/') {
  
  body {
    font-family: IRANSans !important;
    font-weight: 200;
    font-size: 1em;
  }
  
  h1, h2, h3, h4, h5, h6 {
    font-family: IRANSans !important;
  }

  input[type="text"], select, textarea {
    direction: rtl;
  }
  
  h1, h2, h3 {
    font-weight: bold;
  }
  
  h1, h1 span {
    font-size: 1.5rem;
  }
  
  p, .wysiwyg, .wysiwyg p {
    line-height: 1.7em;
  }
  
  .description {
    font-family: IRANSans !important;
  }

  .markdown {
    direction: ltr;
  }
  
  .duty-content textarea {
    direction: rtl;
  }
  
  .duty-content .edit-description, .wiki-content, .wysiwyg {
   direction: rtl;
  }
  
  .profile-timeline .activity-item blockquote {
   direction: rtl !important;
  }
  
  .add-comment textarea {
    direction: rtl;
  }
  
  .edit-subject input {
    direction: rtl;
    font-family: IRANSans;
  }
  
  .view-subject {
    direction: rtl;
    font-family: IRANSans;
    font-size: 1.5rem;
  }
  
  div.user-stories {
    text-align: right;
  }
  
  .user-story-name {
    font-family: IRANSans !important;
    direction: rtl;
  }
  
  .user-story-name a, .wiki-link {
    font-family: IRANSans !important;
    text-align: right;
  }
  
  .wiki-nav li .icon {
    float: left;
  }
  
  .taskboard-task .taskboard-text {
    direction: rtl !important;
    font-family: IRANSans !important;
    text-align: right;
    padding-right: 1rem !important;
    padding-left: 0.5rem !important;
  }

  .taskboard-task .taskboard-text .task-name {
    direction: rtl !important;
    font-family: IRANSans !important;
  }
  
  .taskboard-task .task-num {
    margin-right: 0;
    margin-left: 0.5em;
  }
  
  .taskboard-task-inner {
    direction: rtl;
  }
  
}
