css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
.reblog-list-item .reblog-header .reblog-avatar {
  display:none;
}
.reblog-list-item .reblog-header {
  padding-left:0;
} 
.reblog-list .reblog-list-item .reblog-content {
    margin-left: 0;
    padding-left: 8px;
    border-left: solid 2px #e7e7e7;
}
.reblog-list-item.contributed-content .reblog-header {
  display:none;
}
.reblog-list .reblog-list-item {
  border: none;
  padding-top: 0;
  padding-bottom: 10px;
}
.reblog-list {
  border: none;
}
.xtimestamp {
  margin-bottom: 0;
}
.reblog-list-item .reblog-header .post_info_link::after {
  content: ":";
}
.reblog-list .reblog-list-item .reblog-content figure {
  margin-left: 0;
}

.reblog-list, .reblog-trail--original-post, .reblog-list-item.contributed-content, .reblog-list-item {
    border: none!important;}

.posts .reblog-list+.contributed-content, .posts-holder .reblog-list+.contributed-content, .search_results .reblog-list+.contributed-content, .embeddable-posts .reblog-list+.contributed-content, .mh_post_middle.words .reblog-list+.contributed-content {margin-top:0px;}


#posts .post_content blockquote{margin-bottom:15px;}
.reblog-list-item .reblog-content p:first-child{margin-bottom:15px;}

.post_full .post_content a {
    text-decoration: underline!important;
    background: none!Important;
}
 a.reblog-tumblelog-name.post_info_link {
    font-weight: normal;
    margin-bottom: 0;
    padding-bottom: 0;
}

.reblog-list .reblog-list-item.contributed-content .reblog-content{border:none; padding:0;}


}
