css:
@-moz-document url-prefix("https://codeanywhere.com/") {
body .CodeMirror .cm-tab:not(:first-of-type) {
	background: none !important;
}

body .CodeMirror .CodeMirror-activeline .CodeMirror-linenumber {
	background: #1E282D;
}

body {
	-webkit-user-select: none;
}
/*

body .CodeMirror .CodeMirror-activeline .CodeMirror-guttermarker-subtle {
	width: 26px !important;
}

.CodeMirror-activeline .CodeMirror-gutter-wrapper {
	height: 18px;
	background: #1E282D !important;
	width: 55px;
}

*/

.multisplit .ms-panel .multisplit-content-wrapper .terminal {
    background-color: #263238 !important;
}

.multisplit .imgviewer {
	background: #263238 !important;
}

body .statusbar, .hmenu.sm.sm-simple li a:hover, .hmenu.sm.sm-simple li a.highlighted {
	background: #12181B !important;
}

body .statusbar * {
	border-left: none !important;
}

div.CodeMirror span.CodeMirror-matchingbracket {
	color: #8BC34A !important;
	font-weight: normal !important;
	background: transparent !important;
}

.multisplit .ms-panel .smoothtabs-tabwrap::after {
	background: rgba(0, 0, 0, 0) !important;
}

.multisplit .ms-panel .smoothtabs-tab, .smoothtabs-clone {
	background: rgba(0, 0, 0, 0.2) !important;
}

.loadingoverlay-logo, .multisplit {
	background-color: #263238 !important;
}

.multisplit .ms-panel .multisplit-tabs-wrapper-bg {
	background-color: #1E282D !important;
}

body .multisplit > .ms-panel {
	background: transparent !important;
}

body .CodeMirror .CodeMirror-activeline .CodeMirror-linenumber, .gtree .sticky-node {
	background: transparent !important;
}

.ps-scrollbar-y-rail, .ps-scrollbar-x-rail {
	background: rgba(0, 0, 0, 0.4) !important;
	border-radius: 0 !important;
	right: -3px !important;
	opacity: 1 !important;
	margin-bottom: -3px !important;
}

.ps-scrollbar-y-rail > .ps-scrollbar-y, .ps-scrollbar-x-rail > .ps-scrollbar-x {
	background: rgba(255, 255, 255, 0.2) !important;
}

body .splitter-resize-v {
	border: none;
}

.region-west {
	background-color: #1E282D !important;
}

.region-north, .hmenu.sm.sm-simple {
	background-color: #263238 !important;
}

.multisplit .ms-panel {
	background-color: #263238 !important;
}

.cm-s-default {
	background-color: #263238 !important;
	color: rgba(233, 237, 237, 1) !important;
}

.cm-s-default .CodeMirror-gutters {
	background: #263238 !important;
	color: rgb(83, 127, 126) !important;
	border: none !important;
}

.cm-s-default .CodeMirror-guttermarker, .cm-s-material .CodeMirror-guttermarker-subtle, .cm-s-material .CodeMirror-linenumber {
	color: rgb(83, 127, 126) !important;
}

.cm-s-default .CodeMirror-cursor {
	border-left: 1px solid #f8f8f0 !important;
}

.cm-s-default div.CodeMirror-selected {
	background: rgba(255, 255, 255, 0.15) !important;
}

.cm-s-default.CodeMirror-focused div.CodeMirror-selected {
	background: rgba(255, 255, 255, 0.10) !important;
}

.cm-s-default .CodeMirror-line::selection, .cm-s-material .CodeMirror-line > span::selection, .cm-s-material .CodeMirror-line > span > span::selection {
	background: rgba(255, 255, 255, 0.10) !important;
}

.cm-s-default .CodeMirror-line::-moz-selection, .cm-s-material .CodeMirror-line > span::-moz-selection, .cm-s-material .CodeMirror-line > span > span::-moz-selection {
	background: rgba(255, 255, 255, 0.10) !important;
}

.cm-s-default .CodeMirror-activeline-background {
	background: rgba(0, 0, 0, 0.2) !important;
}

.cm-s-default .cm-keyword {
	color: rgba(199, 146, 234, 1) !important;
}

.cm-s-default .cm-operator {
	color: rgba(233, 237, 237, 1) !important;
}

.cm-s-default .cm-variable-2 {
	color: #80CBC4 !important;
}

.cm-s-default .cm-builtin {
	color: #DECB6B !important;
}

.cm-s-default .cm-atom {
	color: #F77669 !important;
}

.cm-s-default .cm-number {
	color: #F77669 !important;
}

.cm-s-default .cm-def {
	color: rgba(233, 237, 237, 1) !important;
}

.cm-s-default .cm-error {
	color: rgba(255, 255, 255, 1.0) !important;
}

.cm-s-default .cm-string {
	color: #C3E88D !important;
}

.cm-s-default .cm-string-2 {
	color: #80CBC4 !important;
}

.cm-s-default .cm-comment {
	color: #546E7A !important;
}

.cm-s-default .cm-variable {
	color: #82B1FF !important;
}

.cm-s-default .cm-tag {
	color: #80CBC4 !important;
}

.cm-s-default .cm-meta {
	color: #80CBC4 !important;
}

.cm-s-default .cm-attribute {
	color: #FFCB6B !important;
}

.cm-s-default .cm-property {
	color: #80CBAE !important;
}

.cm-s-default .cm-qualifier {
	color: #DECB6B !important;
}

.cm-s-default .cm-tag {
	color: rgba(255, 83, 112, 1) !important;
}

.cm-s-default .CodeMirror-matchingbracket {
	font-weight: bold !important;
}

.CodeMirror-overlayscroll-vertical {
	background: rgba(0, 0, 0, 0.2) !important;
	width: 8px !important;
	display: block !important;
	height: 100% !important;
}

.CodeMirror-sizer {
	padding-right: 8px !important;
}

.CodeMirror-linenumber {
	text-align: center !important;
}

.CodeMirror-foldmarker {
	color: #80CBC4 !important;
	text-shadow: none !important;
}

.cm-wrap .CodeMirror .CodeMirror-activeline .CodeMirror-linenumber {
	background-color: transparent !important;
}

.cm-s-default .CodeMirror-guttermarker, .cm-s-default .CodeMirror-guttermarker-subtle, .cm-s-default .CodeMirror-linenumber {
	color: #537f7e !important;
}
}
