css:
@-moz-document domain("touch.pixiv.net"), domain("dic.pixiv.net") {
/* ad */
.ad-topmost-header,
.ad-container,
.ad-banner,
.ad-banner-r18,
div[id^="nend_adspace"],
.adingoFluctOverlay,
.hide-ads button,
.adfunnel_sp,
div[style*="position: fixed;"],
.pixiv-manga-ad,
.yadsOverlay,

/* premium */
.premium-info-bar,
.premium-icon-before,
.pr-module-container,
.comic-banner,
.premium-color,
.card a[href*="premium_touch.php"],
#sub-header a[href*="premium.php"],
.pixiv_game-list,
.pixiv_game-list ~ center a[href*="ad_group_id"],
.btn-premium-grad-orange,
.popular-search-trial-start,
._mypage-premium-banner,

/* pixiv-comic */
.comic-hot-works {
    display: none !important;
}

/* grid ad */
.grid_ad img {
    display: none !important;
}
.grid_ad {
    background-color: #3A88E4;
    background-repeat: no-repeat;
    background-position: center;
    background-size: 70%;
    background-image: url("http://source.pixiv.net/touch/touch/img/cmn/logo.svg?9a3c37fa81a263a54bfeca53ac63ad7c");
}
.imglist.modethumbnail li.grid_ad:after,
.imglist.ranking li.grid_ad:after {
    background-image: none;
}
}
