/* No ads criado por Ruan Carlos */

@-moz-document domain(unionmangas.net)
{
.cfb8f {
    display: none !important;
}
.d187823 {
    display: none !important;
}
.d187923v {
    display: none !important;
}
#aswift_1 {
    display: none !important;
}
#aswift_1_anchor {
    display: none !important;
}
#aswift_1_expand {
    display: none !important;
}
.adsbygoogle {
    display: none !important;
}
.iframe_uolcliques {
    display: none !important;
}
.ads {
    display: none !important;
}
.navbar-form:after {
    content: attr(title);
    padding: 4px 8px;
    color: #333;
    position: absolute;
    left: 0;
    top: 100%;
    white-space: nowrap;
    z-index: auto;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
    -moz-box-shadow: 0px 0px 4px #222;
    -webkit-box-shadow: 0px 0px 4px #222;
    box-shadow: 0px 0px 4px #222;
    background-image: -moz-linear-gradient(top, #eeeeee, #cccccc);
    background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0, #eeeeee), color-stop(1, #cccccc));
    background-image: -webkit-linear-gradient(top, #eeeeee, #cccccc);
    background-image: -moz-linear-gradient(top, #eeeeee, #cccccc);
    background-image: -ms-linear-gradient(top, #eeeeee, #cccccc);
    background-image: -o-linear-gradient(top, #eeeeee, #cccccc);
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}
.slides {
    background-image: url("http://unionmangas.com.br/forum/public/style_images/Union_2_0/backgrounds/background1bcd.jpg");
    background-repeat: no-repeat;
    background-color: #4C4C4E;
    background-attachment: fixed;
}
.btn-success {
    display: none;
}
#bloco-principal {
    width: 1155px;
}
.col-md-8 {
    width: 1155px;
}
#frm_login,
#frm_registro {
    text-align: center;
}
#email,
#password,
#nome,
#nome2,
#rpassword {
    width: 600px;
    display: block;
    margin-left: auto;
    margin-right: auto;
}
h2 {
    text-align: center;
    display: block;
    margin-left: auto;
    margin-right: auto;
}
body {
    background-image: url("http://unionmangas.com.br/forum/public/style_images/Union_2_0/backgrounds/background1bcd.jpg");
    background-repeat: no-repeat;
    background-color: #4C4C4E;
    background-attachment: fixed;
}
.avoid-clicks {
    pointer-events: none !important;
}
.content-site {
    pointer-events: all;
}
.navbar-form:after {
    content: "SS by RuanCarlos (v2.0)";
    color: green;
    font-size: 90%;
}
::-webkit-scrollbar {
    width: 12px;
}
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
    border-radius: 10px;
}
::-webkit-scrollbar-thumb {
    border-radius: 10px;
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.5);
}
::-webkit-scrollbar {
    width: 12px;
    height: 12px;
}
.row .lancamento-linha {
    border-bottom: none;
}
.link-menu:hover {
    border-bottom: 3px solid black;
}
/* .navbar-brand:hover {
    background-image: url("http://i.imgur.com/6RuFwpi.gif")
} */
.row .col-md-4 .list-group,
.row .col-md-4 .lancamento-linha,
.btn-default,
.col-md-4 .text-center,
.sidebar-content,
.col-md-4 h2 {
    display: none;
}
.img-responsive {
    display: none;
}
.col-md-perfil {
    width: 1155px;
    display: block;
    margin-left: auto;
    margin-right: auto;
}
.img-thumbnail {
    border-radius: 0%;
    display: block;
    margin-left: auto;
    margin-right: auto;
}
.col-md-2 .img-thumbnail:hover,
.bloco-manga .img-thumbnail:hover {
    border: 1px solid black;
    border-bottom: 3px solid red;
    -webkit-transform: scale(1.60);
    -moz-transform: scale(1.60);
    -ms-transform: scale(1.60);
    -o-transform: scale(1.60);
    transform: scale(1.60);
}
.col-md-12 img {
    border: 1px solid black;
}
.manga-perfil {
    border: none;
    border-top: none;
    border-bottom: none;
}
.slide-body,
.slides,
.slides img,
.slide-body img {
    width: 1125px;
    height: 400px;
    display: block;
    margin-left: auto;
    margin-right: auto;
}
.col-md-8 .tamanho-bloco-perfil:target {
    height: 10px;
    display: block;
    margin-left: auto;
    margin-right: auto;
}
#top {
    height: 53px;
}
.alert {
    border-radius: 0%;
    text-align: center;
}
active:target {
    border-bottom: 3px solid black;
}
.btn,
.selectPage,
#cap_manga1,
#cap_manga2 {
    border-radius: 0%;
}
