css:
@-moz-document domain(twitter.com) {
  @media screen and (max-width: 890px){
    .wrapper{
      width: auto;
    }
    .Trends, .wtf-module.has-content, .Footer,
    .component[data-component-context="newest_list_members"],
    .component[data-component-context="more_lists"],
    .PhotoRail, .MoveableModule, .WhoToFollow {
      display: none !important;
    }
    .ProfilePage .AppContent{
      overflow: hidden;
    }
    .dashboard, .content-main{
      float: none;
      width: auto;
    }
    .ProfilePage{
      min-width: 0;
    }
    .AppContainer{
      max-width: none;
    }
    .ProfilePage .Grid-cell.u-size2of3.u-lg-size3of4{
      width: 100% !important;
    }
    .ProfilePage.three-col .AppContent-main.content-main{
      margin: 0 10px
    }

    .ProfileCanopy-navBar .Grid-cell.u-size1of3.u-lg-size1of4 {
      display: none;
    }

    .ProfileCanopy-navBar .Grid-cell.u-size2of3.u-lg-size3of4 {
      width: auto !important;
      width:  100% !important;
    }

    .ProfileCanopy-avatar {
      bottom: -350px;
      position: relative;
      display: inline-block;
    }

    .ProfileSidebar.ProfileSidebar--withLeftAlignment {
      margin: 10px 0 10px 220px;
      min-height: 180px;
    }

    .ProfileCanopy-nav {
      margin: 0 10px;
    }

    .AppContent-main .Grid-cell.u-size1of3.u-lg-size1of4 {
      width: auto !important;
    }
    .ProfileHeaderCardEditing {
      margin-top: 0;
    }

    .ProfileHeaderCard {
      margin-top: 20px;
    }

    .AdaptiveSearchPage .AppContent-main {
      min-width: 0;
      max-width: none;
    }

    .AdaptiveSearchPage .AppContent {
      min-width: 0;
      overflow: hidden;
    }

    .AdaptiveSearchPage .Grid-cell.u-size2of3.u-lg-size3of4 {
      width: 100% !important;
      padding: 0 15px;
    }
    .global-nav .t1-form.form-search {
      width: 200px;                  
    }
  }
}
