@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.zoho.com") {
  body.font3, body.font3 .SC_pv, body.font3 .zmL, body.font3 .ztb-popover-label, body.font3 input, body.font3 select, body.font3 td, body.font3 textarea {
    font-size: 12px;
  }
  .SC_tre span {
    font-size: 12px;
  }
  body .zm_comp span {
    font-size: 12px;
  }
  .zmSub span {
    font-weight: 700;
  }
  .zmFdr {
    width: 75px;
  }
  .zmDate {
    width: 50px;
  }
  .zm_urd.zmL .zmFrm {
    font-size: 100%;
  }
  .zmFrm {
    white-space: nowrap;
    font-size: 100%;
}
  .SC_cmpct > .zmL .zmFlt, .SC_cmpct > .zmL .zmSub,
  .SC_cmpct div:not(.zmTV) > .zmL .zmFlt, .SC_cmpct div:not(.zmTV) > .zmL .zmSub {
    padding-top: 0px;
  }
  .SC_cmpct > .zmL .zmFrm,
  .SC_cmpct div:not(.zmTV) > .zmL .zmFrm {
    padding-top: 1px;
  }
  .SC_cmpct > .zmL .zmFlt,
  .SC_cmpct div:not(.zmTV) > .zmL .zmFlt {
    width: 65px;
  }
  .SC_cmpct .zmL .msi-thread {
    top: 18px;
  }
  .SC_cmpct > .zmL .zmflg,
  .SC_cmpct div:not(.zmTV) > .zmL .zmflg {
    top: 15px;
  }
  .zmL .zmflg {
    left: 8px;
  }
  .zmL .zmFlt i {
    width: 14px;
  }
  .SC_cmpct > .zmL,
  .SC_cmpct div:not(.zmTV) > .zmL {
    height: 34px;
  }
  
  .zmFrm {
    padding: 0 0;
  }
  .SC_cmpct > .zmL .zmFlt .msi-fwd, .SC_cmpct > .zmL .zmFlt .msi-mail, .SC_cmpct > .zmL .zmFlt .msi-rpd,
  .SC_cmpct div:not(.zmTV) > .zmL .zmFlt .msi-fwd, .SC_cmpct div:not(.zmTV) > .zmL .zmFlt .msi-mail, .SC_cmpct div:not(.zmTV) > .zmL .zmFlt .msi-rpd
  {
    top: 1px;
  }
  
  .zm_urd.zmL .msi-fwd, .zm_urd.zmL .msi-mail, .zm_urd.zmL .msi-rpd {
    position: absolute;
    margin-left: 0px;
    left: 38px;
  }
  .SC_cmpct  > .zmL .zmFlt .msi-fwd, .SC_cmpct  > .zmL .zmFlt .msi-mail, .SC_cmpct  > .zmL .zmFlt .msi-rpd,
  .SC_cmpct div:not(.zmTV) > .zmL .zmFlt .msi-fwd, .SC_cmpct div:not(.zmTV) > .zmL .zmFlt .msi-mail, .SC_cmpct div:not(.zmTV) > .zmL .zmFlt .msi-rpd {
    position: absolute;
    margin-left: 0px;
    left: 38px;
  }
  .SC_cmpct.SC_sw > .zmL .SC_icn,
  .SC_cmpct.SC_sw div:not(.zmTV) > .zmL .SC_icn {
    bottom: 9px;
    margin-right: -18px;
  }
  
  .SC_tre {
    padding: 3px 2px 4px 19px;
  }
  .SC_cmpct > .zmL .zmFrm,
  .SC_cmpct div:not(.zmTV) > .zmL .zmFrm {
    width: 130px;
  }
  .SC_cmpct > .zmL .SC_dft span,
  .SC_cmpct div:not(.zmTV) > .zmL .SC_dft span {
    right: 100px;
    font-size: 72%;
  }
