css:
/* Inicio do modo No ADs */

@-moz-document domain(yesmangas.com)
{
#aswift_0 {
    display: none !important;
}
#aswift_0 {
    display: none !important;
}
#aswift_1 {
    display: none !important;
}
#aswift_0_anchor {
    display: none !important;
}
#aswift_1_anchor {
    display: none !important;
}
#aswift_0_expand {
    display: none !important;
}
#aswift_1_expand {
    display: none !important;
}
.adsbygoogle {
    display: none !important;
}
.ads {
    display: none !important;
}
.ads ads-left {
    display: none !important;
}
.ads ads-right {
    display: none !important;
}
#sidebar:after {
    content: " SS by RuanCarlos ";
    color: green;
    font-size: 100%;
}
#sidebar {
    display: none;
}
#lancamentos {
    width: 978px;
}
#capitulos {
    width: 879.188px;
}
#destaques {
    width: 978px;
}
.navbar:after {
    content: " SS by RuanCarlos (v1.6)";
    color: green;
    font-size: 90%;
    padding: 4px 8px;
    position: absolute;
    left: 0;
    top: 100%;
    white-space: nowrap;
    z-index: auto;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
    -moz-box-shadow: 0px 0px 4px #222;
    -webkit-box-shadow: 0px 0px 4px #222;
    box-shadow: 0px 0px 4px #222;
    background-image: -moz-linear-gradient(top, #eeeeee, #cccccc);
    background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0, #eeeeee), color-stop(1, #cccccc));
    background-image: -webkit-linear-gradient(top, #eeeeee, #cccccc);
    background-image: -moz-linear-gradient(top, #eeeeee, #cccccc);
    background-image: -ms-linear-gradient(top, #eeeeee, #cccccc);
    background-image: -o-linear-gradient(top, #eeeeee, #cccccc);
}
.u-full-width img {
    width: 180px;
    height: 150px;
    display: block;
    margin-left: auto;
    margin-right: auto;
}
.u-full-width img:hover {
    -webkit-transform: scale(1.15);
    -moz-transform: scale(1.15);
    -ms-transform: scale(1.15);
    -o-transform: scale(1.15);
    transform: scale(1.15);
    border: 2px solid black;
}
.u-full-width .button:hover,
#capitulos .button:hover {
    border: 1.3px solid black !important;
    border-bottom: 4px solid red !important;
    background-color: #F8F8F8;
    -webkit-transform: scale(1.25);
    -moz-transform: scale(1.25);
    -ms-transform: scale(1.25);
    -o-transform: scale(1.25);
    transform: scale(1.25);
}
#outros .button,
.u-full-width .button,
#capitulos .button,
#destaques .button {
    border-radius: 0%;
}
#outros .button:hover {
    border-right: 1.3px solid black !important;
    border-left: 1.3px solid black !important;
    border-bottom: 4px solid green !important;
}
#destaques .button:hover {
    border-right: 1.3px solid black !important;
    border-left: 1.3px solid black !important;
    border-bottom: 4px solid #3879D9 !important;
}
#destaques .last_chapter:hover {
    border-bottom: 4px solid red !important;
}
.chapters,
.images,
.prev,
.next {
    border-radius: 0%;
}
}
