@-moz-document 
  domain("s1.kickerstar.de"), domain("s2.kickerstar.de"), domain("s3.kickerstar.de"),
  domain("s4.kickerstar.de"), domain("s5.kickerstar.de"), domain("s6.kickerstar.de"),
  domain("s7.kickerstar.de"), domain("s8.kickerstar.de"), domain("s9.kickerstar.de"),
  domain("w1.soccerstar.com")
{

  #body 
  { /* Normales Hintergrundbild */
    background: #3a7227 url("http://img.playa-games.com/res/soccerstar/soccerstar_background.jpg") no-repeat center top !important;  
    text-align: center; 
  }
  html, body
  { /* Scroll-Balken */
    overflow: initial !important;
  }
