css:
@-moz-document domain("learnopengl.com") {
div#container div#content {
    max-width: 40em;
    margin: 0 auto 0 auto;
}

div#container div#content p,
div#container div#content li,
div#container div#content note,
div#container div#content warning,
div#container div#content info,
div#container div#content error,
div#container div#content img,
div#container div#content pre
{
    max-width: 40em;
}
}
