css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("coub.com") {
body { background: #000 !important; color: #f0f0f0 !important; }
.header__bg { background: #080808 !important; }
.-bg-color--white { background: #040404 !important; }
.profile__channel-panel, .header.-scrolled { box-shadow: 0px 1px 0px #232323 !important; }
.coub__description { background-color: rgba(255,255,255,.2) !important; }
.coub .coub__vd { background-color: transparent !important; }
.coub__tags--timeline .coub__tags-cropper, .coub__tags--timeline .coub__tags-cropper:hover, .coub__tags--timeline .coub__tags-cropper:hover:active { background: rgba(45,45,45,.6) !important; }
a { color: #f0f0f0 !important; }
a:hover, .-color--blue-ribbon { color: #1e39f6 !important; }
.channel__relationships .sb-group--profile .sb { background-color: rgba(255, 255, 255, 0.2) !important; }
.sb.-st{ color: rgba(255, 255, 255, 0.7) !important; background-color: rgba(255,255,255,.2) !important; }
.header-channel__notify-badge .sb.-notify-handler { background: rgba(255,255,255,.2) !important; }
.dropdown__content { background-color: rgba(64,64,64,.4) !important; }
.dropdown__content > ul { text-shadow: 0px 0px 3px #fff !important;}
.box-card { background-color: rgba(255,255,255,.2) !important; }
.coub__tags--page .coub__tags-wrap > a { background-color: rgba(255,255,255,.2) !important; border: 1px solid #111111 !important; } .coub__tags--page .coub__tags-wrap > a:hover { border: 1px solid #1e39f6 !important; }
.modal--simple .modal__content { background-color: rgba(0,0,0,.8) !important; }
li > a:hover { color: #fff !important; }
button.coub__tags-cropper { background-color: rgba(255,255,255,.2); border: 1px solid #111111; }
.-bg-color--mercury { background-color: rgba(32,32,32,.6); }
.box--banner { background-color: rgba(255,255,255,.2) !important; }
.iphone-banner { display: none !important; }
.settings__link-field { background-color: #000; }
.input-field { border: 1px inset #888; }
.settings__auth.connected > .settings__auth-button, .input-field-group.-with-prefix > input { background-color: rgba(255,255,255,.2) !important; }
.input-field-group.-with-prefix { background: #000 !important; }
.gender-pick > .sb { background-color: rgba(255,255,255,.2); }
.date-select i { background-color: rgba(255,255,255,.2); }
.cb-edtr__upload__buttons { background-color: rgba(255,255,255,.2); }
.cb-edtr__process--initial { background-color: #f7f7f7; }
.corner { border-color: #000; }
.cb-edtr__timeline-wrap::before, .cb-edtr__timeline-wrap::after { box-shadow: 0 0px 40px 55px #020202; }
.coub__tags--timeline .coub__tags-wrap::after { box-shadow: -10px 0px 20px 10px #333333; background: #333333; }
h1 { color: #EEE; }
.related-tags__wrap > a:hover { text-shadow: 0px 0px 2px #fff !important; }
.related-tags__cropper.sb { color: #123; }
.popular-page__nav { background-color: rgba(255,255,255,.2) !important; border-top: 1px solid #111111 !important; }
.header__create-btn .sb.create:hover { color: #fff !important; }
footer.footer .footer__copyright { color: rgba(255, 255, 255, 0.7); }
.cb-edtr__sharing__text { color: rgba(255, 255, 255, 0.4); }
.nav-tooltip__control { color: rgba(255, 255, 255, 0.5); }
}
