css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.patreon.com")

{

.boxWrapper, .shareWindow, .boxExtra
{
background-color: rgba(0, 0, 0, 0) !important;
}

p.info a
{
font-size: 16px !important;
font-family: Calibri !important;
background-color: rgba(0, 0, 0, 0) !important;
-webkit-transition: all 0.3s ease-in-out !important;
-moz-transition: all 0.3s ease-in-out !important;
-o-transition: all 0.3s ease-in-out !important;
-ms-transition: all 0.3s ease-in-out !important;
transition: all 0.3s ease-in-out !important;
}

p.info a:hover, a:hover
{
color: #000000 !important;
text-decoration: none !important;
}

a, .imageOver, input.submitForm
{
-webkit-transition: all 0.2s ease-in-out !important;
-moz-transition: all 0.2s ease-in-out !important;
-o-transition: all 0.2s ease-in-out !important;
-ms-transition: all 0.2s ease-in-out !important;
transition: all 0.2s ease-in-out !important;
}

#categoryMenu li:hover
{
border-bottom: none !important;
}

#categoryMenu li.active
{
border-bottom: 4px solid #2A5096 !important;
}

.boxExtra
{
margin-top: 0px !important;
}

.boxUserImage img
{
margin-top: 2px !important;
}

.shareWindow a.moreText
{
z-index: 999 !important;
bottom: -5px !important;
width: 310px !important;
padding-top: 0px !important;
text-align: right !important;
background-color: #F2F1EF !important;
-webkit-transition: all 0.3s ease-in-out !important;
-moz-transition: all 0.3s ease-in-out !important;
-o-transition: all 0.3s ease-in-out !important;
-ms-transition: all 0.3s ease-in-out !important;
transition: all 0.3s ease-in-out !important;
}

.shareWindow a.moreText:hover
{
color: #000000 !important;
}

.tabTri.attach
{
background-size: 30px 30px !important;
width: 30px !important;
height: 30px !important;
margin-top: 9px !important;
right: 15px !important;
}

.boxWrapper p, h3
{
text-align: justify !important;
}

.boxWrapper p
{
line-height: 15px !important;
}

h3
{
line-height: 23px !important;
}

.comments
{
top: 45px !important;
background-color: rgba(0, 0, 0, 0) !important;
border: none !important;
}

.boxUserImage.small img
{
width: 15px !important;
height: 15px !important;
margin-top: -2px !important;
}

p.commenter
{
margin-top: -2px !important;
margin-bottom: -3px !important;
}

p.commentDate
{
font-size: 9px !important;
}

.comments .commentBox
{
margin-bottom: -7px !important;
padding: 5px !important;
font-size: 12px !important;
}

}
