@-moz-document domain(feedly.com) {

/* Background */
  #feedlyFrame, body, #realtimeSign, #mainBarFX {
    background: #424242 !important;
  }
  
  /* Colors */
  #floatingBar, #fixedProfile, .websiteCallForAction, #bigMarkAllAsReadButton {
    background: #5a5a5a !important; /* Green */
    color: #AAA !important;
  }
  #floatingBar span {
    color: #CCC !important;
  }
  .headerInfo-article {
    background: none !important;
  }
  
  /* Remove right sidebar */
  #sideArea, #herculeFrame, #searchBoxPlaceholder{
    display: none !important;
  }

  /* Adjust Main Section */
  #feedlyPage {
    width: 100% !important;
  }
  .u4Entry {
    max-width: 100% !important;
  }
  
  /* Invert buttons colors */
  #pageActionMarkAsRead, #pageActionRefresh, #pageActionCustomize, .pageAction, .quicklistHandle, .condensedTools a, .condensedTools img, .headerInfo-article img, .headerInfo-expanded-img, .icon-toolbar-add-to-board {
    filter: invert(100%) !important;
    -webkit-filter: invert(100%) !important;
  }
  
  /* Texts */
  h1, h2, a:hover, a.unread:hover, .action:hover, #lvcOptions div:hover {
    color: #EEE !important;
  }
  a.unread, .label, #feedlyPageFX h3, .editionInfo {
    color: #DDD !important;
    text-decoration: none !important;
  }
  a, .action, .priceTag {
    color: #BBB !important;
    font-weight: bold !important;
    text-decoration: none !important;
  }
  p, span, .entriesLoadingMessage, .preferencesPanel *, #lvcOptions{
    color: #AAA !important;
  }
  
  /* Add an arrow before an unread article headline */
  a.unread:before {
    content: "â˜ž" !important;
    font-size: 16px !important;
  }
  
  /***********/
  /* Sidebar */
  /***********/
  
  /* Background */
  #feedlyTabs {
    background: #212121 !important;
  }
  
  /* Remove unnecessary buttons: Share collections, Intergrations, Support, Privacy Policy, Term Of Service and feedly academy */
  #librarytab_header, #integrationstab_header, .label.primary.iconless[data-uri="pro/support"], .label.primary.iconless[data-uri="subscription/feed/https://blog.feedly.com/tag/academy/feed/"], .label.primary.iconless[href="https://feedly.com/i/privacy"], .label.primary.iconless[href="https://feedly.com/i/terms"]{
    display: none !important;
  }
  
  /* Adjust spacings */
  #feedlyTabs > div:nth-child(2), #feedlyTabs > div:nth-child(3) {
    margin-bottom: 14px !important;
  }
  
  /* Sidebar buttons */
  #feedlyTabsUnpin, .fx-button.primary.small, .fx-button, .hAction {
    transition-duration: .5s !important;
    color: #AAA !important;
    border: 1px #888 solid !important;
    background: none !important;
    box-shadow: none !important;
  }
  #feedlyTabsUnpin:hover, .fx-button.primary.small:hover, .fx-button:hover, .hAction:hover {
    color: #CCC !important;
    border: 1px #CCC solid !important;
    text-decoration: none !important;
  }
  
  /* Sidebar items */
  .header, .feedIndex, .label.primary.iconless, .moreHandle {
    transition-duration: .25s !important;
    border-radius: 3px !important;
    padding-right: 6px !important;
  }
  .header:hover, .feedIndex:hover, .label.primary.iconless:hover, .moreHandle:hover {
    background: #555 !important;
    margin-left: 3px !important;
  }
  .selected * {
    color: #EEE !important;
    transition-duration: .25s !important;
    font-weight: bold !important;
  }
  
  /* Remove unread numbers background when hovering */
  .simpleUnreadCount {
    background: none !important;
  }
  
  /* Articles background */
  .inlineFrame, .entryholder, .floatingEntryScroller {
    background: #101e2b !important;
  }
  
  
  /***************/
  /* Titles only */
  /***************/
  
  .u0Entry {
    border-bottom: 1px solid #666 !important;
    margin-top: 1px !important;
  }
  .u0Entry:hover {
    background: #444 !important;
  }

  /* Saved articles */
  .quicklisted {
    background: none !important;
  }
  
  /************/
  /* Magazine */
  /************/
  
  .selectedEntry{
    background: #212121 !important;
    border-radius: 4px !important;
  }
    
  .u4Entry {
    border-bottom: 1px solid #666 !important;
    margin-bottom: 16px !important;
    padding-bottom: 8px !important;
    transition-duration: .25s !important;
  }
  .u4Entry:hover {
    border-bottom: 1px solid #AAA !important;
  }
  
  /*********/
  /* Cards */
  /*********/
  
  .u5Entry, .u100Entry, .edition {
    background: #424242 !important;
  }
  
  .u100Frame {
    background: #424242 !important;
  }
  
  /**********/
  /* Others */
  /**********/
  
  /* Today page */
  #searchBarFX {
    background: #212121 !important;
  }
  
  /* Popups */
  .headerInfo-expanded-scroller {
    background: #222 !important;
  }
  .headerInfo-arrow {
    filter: invert(86.5%) !important;
    -webkit-filter: invert(86.5%) !important;
  }
  
  .popup {
    background: #212121 !important;
    box-shadow: 3px 5px 8px 0px rgba(0,0,0,0.7) !important;
  }
  
  /* Slide article */
  .floatingEntryScroller, .entryholder, .headerInfo {
    background-color: #424242 !important;
  }
  
  .headerInfo {
    border-bottom: solid 1px #212121 !important;
    box-shadow: 2px 2px 5px 2px rgba(0,0,0,0.4) !important;
  }
  
  .slideBumper {
    border-radius: 30px !important;
    background: #888 !important;
  }
  
  /* NO BORDERS */
  * {
    border: none !important;
  }

/* Propios barra */

#headerBarFX {
    background-color: transparent;
}

.fx h1 {
    margin-top: 0rem;
}

.fx .button-refresh,.fx .button-markasread {
    background-color: rgba(165, 165, 165, 0.67);
    border-radius: 2em;
    margin-right: 0.5em;
    margin-bottom: 0.5em;
}

/* Letras */

.slabserif .fx .entry.u4 .summary {
    font-family: verdana;
}

a, .action, .priceTag {
    color: #fff !important;
}

#feedlyPageFX .heading {
	color: white !important;
    margin-top: 1em;
}

div .sub {
	display: none;
}

.stream h4 {
    margin-top: 0.5rem !important;
}

.fx header {
    margin-bottom: 0rem !important;
}

.fx .button-markasread:hover, .fx .button-refresh:hover {
    background-color: #fff;
}

.header:hover, .feedIndex:hover, .label.primary.iconless:hover, .moreHandle:hover {
    margin-left: auto !important;
}

#feedlyTabsHolder.pinned #feedlyTabs {
    overflow: hidden;
}
    
}
