css:
@-moz-document domain("slack.com") {
body {
    height: 100%;
    width: 100%;
    min-width: 8in;
    font-family: "Helvetica Neue",Helvetica,"Segoe UI",Tahoma,Arial,sans-serif;
    -webkit-font-smoothing: antialiased;
    font-size: 1pc;
    line-height: 1rem;
    background: #0E0E0E;
    color: #555459;
    padding: 0;
    overflow-y: hidden;
    overflow-x: hidden;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;}
    #team_menu, #quick_switcher_btn, #team_menu_overlay, #col_channels_overlay {
    background: #000000;
    border-color: #191919;
}
#team_menu {
    color: #FFFFFF;
    text-shadow: 0 1px 1px rgba(0, 0, 0, 0.25);}
#col_channels ul li.channel.active a.channel_name, #col_channels ul li.group.active a.group_name, #col_channels ul li.member.active a.im_name, #col_channels ul li.mpim.active a.mpim_name {
    background: #555456;
    opacity: 1;
    color: #FFFFFF !important;
}
.channels_list_holder ul li a, .channels_list_holder h2, .list_more, #team_menu:before, .feature_dnd_client #dnd_status, .channels_list_holder ul li.channel.active a.channel_name .prefix, .channels_list_holder ul li.group.active a.group_name .prefix, #current_user_name, .channels_list_holder ul li.unread .prefix, .channels_list_holder ul li.member .im_close, .channels_list_holder ul li.mpim .mpim_close, .channels_list_holder ul li.group .group_close, #quick_switcher_btn > i {
    color: #FFFFFF !important;
    opacity: 0.6;}
body:not(.feature_new_message_markup) .day_divider {
    background: #0E0E0E;
    margin: 1rem -18px 1rem -1.5rem;
    padding: .5rem 18px .5rem 1.5rem;
    line-height: 1.2rem;
    font-size: 1rem;
    font-family: Slack-Lato,appleLogo,sans-serif;
    color: #555459;
    font-weight: 900;
    text-align: center;
    cursor: default;
    clear: both;
    position: relative;}
body:not(.feature_new_message_markup) .day_divider_label {
    background: #0E0E0E;
    padding: 0 1rem;
    display: inline-block;
    margin: 0 auto;
    position: relative;
}
hr {
    border: 0;
    border-top: 1px solid #eee;
    border-bottom: 1px solid #555456;
}
.light_theme .message {
    padding: .25rem 2rem .1rem 3rem;
    font-family: Slack-Lato,appleLogo,sans-serif;
    font-size: 15px;
    line-height: 22px;
    color: #FFFFFF;}
#header {
    background: #0E0E0E;
    height: 53px;
    position: fixed;
    z-index: 100;
    min-width: 8in;
    width: 100%;}
#channel_members_toggle {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    color: #F7F7F7;
    font-size: 15px;
    font-weight: 700;
    padding: .5rem;
    background: #0E0E0E;
    position: absolute;
    -webkit-transition: color .1s ease-out 0s;
    -moz-transition: color .1s ease-out 0s;
    transition: color .1s ease-out 0s;
    font-family: Slack-Lato,appleLogo,sans-serif;
    z-index: 25;
}
#footer {
    position: fixed;
    background: #0E0E0E;
    bottom: 0;
    left: 220px;
    right: 1rem;
    height: 4rem;
    z-index: 52;}
#message_edit_form #message-input, #messages-input-container #message-input, .inline_message_input_container #message-input {
    background: #0E0E0E;
    width: 100%;
    font-size: 15px;
    line-height: 1.2rem;
    border: 2px solid #555459;
    border-left: none;
    background-clip: padding-box;
    font-family: Slack-Lato,appleLogo,sans-serif;
    margin: 0;
    color: #FFFFFF;
    -webkit-appearance: none;
    box-shadow: none;
    outline: 0;
    position: absolute;
    bottom: 0;
    height: 100%;
    min-height: 42px!important;
    padding: 9px 40px 9px 8px;
    max-height: 10pc!important;
    resize: none!important;
    -webkit-transition: none;
    -moz-transition: none;
    transition: none;
    border-radius: 0 6px 6px 0;}
#primary_file_button {
    position: absolute;
    bottom: 0;
    left: -44px;
    background: #0E0E0E;
    color: #FFF;
    border: 2px solid #555456;
    background-clip: padding-box;
    height: 42px;
    width: 44px;
    line-height: 42px;
    min-height: 42px!important;
    max-height: 10pc!important;
    padding: 0;
    text-align: center;
    -webkit-transition: border-color 50ms,background 50ms,color 50ms;
    -moz-transition: border-color 50ms,background 50ms,color 50ms;
    transition: border-color 50ms,background 50ms,color 50ms;
    text-shadow: none;
    border-radius: 6px 0 0 6px;
}
#col_channels {
    background: #000000;
}
}
