css:
@-moz-document domain("oa.bgu.edu.cn") {
.iwk_top_head {
    width: 100%;
}
.content {
    width:100% !important;
    margin:0 !important;
    background: #DBF2FD;
}
.iwk_left_item_ttl {
    width: 90px;
}
#iwk_page {
    width: 80%;
    background: none;
}
#iwk_left {
    width: 200px;
}
/*头部背景*/

.iwk_top_head {
    background: none;
}
#iwk_banner:after {
    display: block;
    clear: both;
    content: "";
    visibility: hidden;
    height: 0;
}
#iwk_banner,
#iwk_wide_right,
#iwk_wide_right.org_bg {
    width: 1200px;
    float: left;
}
#docs_tree {
    width: 200px;
}
#tree_member_12_switch {
    background-position: 0 0;
}
#tree_member_12_ul {
    display: block;
}

#docs_show{
    float:left;
    width:950px;
}

.docs_header>:first-child{
    width:580px !important;
}

.single_docs .name{
    width:470px !important;
}

.single_docs >:first-child{
    width:580px !important;
}


.single_div_img{
    width:26px !important;
    margin-right:5px;
}

.single_docs .docs_th a{
    float:right;
}

#iwk_center{
    width:800px;
}

#iwk_right{
    float:left;
    margin-left:50px;
}


}
