css:
@-moz-document domain("youtube.com") {
/*@namespace url(http://www.w3.org/1999/xhtml);*/

.video-extras-sparkbar-likes {
    background: LimeGreen none repeat scroll 0% 0%;
}
.video-extras-sparkbar-dislikes {
    background: #E34848 none repeat scroll 0% 0%;
}
.like-button-renderer-like-button.yt-uix-button.yt-uix-button-toggled::before {
    background: transparent url("http://i.imgur.com/6R4P9m8.png");
}
.like-button-renderer-like-button.yt-uix-button-toggled .yt-uix-button-content {
    color: green;
}
.like-button-renderer-dislike-button.yt-uix-button-toggled::before {
    background: transparent url("http://i.imgur.com/wNQfcMI.png");
}
.like-button-renderer-dislike-button.yt-uix-button-toggled .yt-uix-button-content {
    color: red;
}
/*.watch-view-count {
      color: #1480CD;
  }*/

.comments .like-count {
    color: green;
}
.comments .sprite_like_on {
    background: transparent url("http://i.imgur.com/Q5AA3au.png");
}
.comments .sprite_dislike_on {
    background: transparent url("http://i.imgur.com/9gOOZuh.png");
}
.comments .sprite_like {
    background: transparent url("http://i.imgur.com/OmmapdQ.png");
}
.comments .sprite_dislike {
    background: transparent url("http://i.imgur.com/UkS3Mzo.png");
}
}
