@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("web.whatsapp.com") {
  
  /* Remove intro image, enable desktop notifications, bubble corner */
  /* small line after chat header and bubbles tail in chat boxes */
  .butterbar-notification, .message:before, header.pane-chat-header:after, .tail-container {
    display: none !important;
  }
  
  /* Move sidebar scrollbar to the left */
  #pane-side {
    direction: rtl !important;
  }
  .chat {
    direction: ltr !important;
  }
  
  /* icons color invert*/
  span[data-icon="down-context"],
  span[data-icon="down"],
  span[data-icon="status-vcard"],
  span[data-icon="status-image"],
  span[data-icon="status-video"],
  span[data-icon="smiley"],
  span[data-icon="hide"],
  span[data-icon="ptt"],
  span[data-icon="emoji-gifs"],
  .menu-horizontal button > span, .media-panel-tools > .menu-item > span {
    filter: invert(100%) !important;
    -webkit-filter: invert(100%) !important;
  }
   .icon-status-dblcheck-ack, .icon-msg-dblcheck-ack, .icon-ptt-in-blue, .icon-ptt-out-blue, .icon-logo, .icon-platform {
    filter: none !important;
    -webkit-filter: none !important;
  }
   
  /* Background */
  .app-wrapper, #startup, #wrapper, #wrapper:before {
    background: #444242 !important;
  }  
  .media-viewer-thumbs, .multi-controls, .well-chat, .drawer-section well {
    background: #212121 !important;
  }
   
  /* Login page */
  #window, #platforms {
    background: #303030!important;  
  }
  #window *, #platforms * {
    color: #DDD !important;
  }
  .qrcode {
    border: 16px solid white !important;
  }
  
  /* Texts */
  .chat-title, .action .button-contact, .title, .header-title, .input-text {
    color: #EEE !important;
  }
  .chat-status, .message-text, .msg-search-notfound, .well, .pane-chat-header-subtitle, ._3sgkv.Gd51Q {
    color: #CCC !important;
  }
  .message-datetime, .chat-meta {
    color: #999 !important;
  }
  
  /* Checkboxs */
  .checkbox.unchecked {
    background: #EEE !important;
  }
  .checkbox.checked {
    background: #00BFA5 !important;
  }
  
  /***********/
  /* Sidebar */
  /***********/
  
  .input-main, .placeholder {
    color: #BBB !important;
    padding-right: 20px !important;
  }
  .btn-round {
    background: #00BFA5 !important;
  }
   
  /* Search bar and right sidebar */
  .subheader-search, .subheader-search:after, #pane-side, .pane-preview, .animate-enter2, .animate-enter, .chatlist-panel-search {
    background: #333030!important;
  }
  .cont-input-search, .input-search, .animate-enter1 {
    background: #DDD !important;
  }
  
  /* Chats list */
  .section-header {
    background: #333030 !important;
    text-align: center !important;
  }
  
  .chat, .infinite-list-item, .list-title {
    background: #333030 !important;
  }
  .chat:after {
    border-bottom: 1px solid #444242 !important;
  }  
  .active.chat {
    background: #444242 !important;
  }
  .hover.chat {
    background: #666 !important;
  }
  
  /* Profile & status */
  header.drawer-header-small {
    background: #4caf50 !important;
    color: #EEE !important;
  }
  .drawer-body {
    background: #333030!important;
  }
  .input::placeholder {
    color: #303030 !important;
  }
   
  .drawer-section-body {
    padding-top: 0 !important;
  }
  
  /**************/
  /* Intro page */
  /**************/
  
  .pane.pane-intro.pane-two {
    background: #444;
  }
  
  .pane-intro:after {
    border-top: 6px solid #388e3c !important;
  }
  
  /* Cut white parts from image */
  .intro-image {
    border-radius: 50%!important;
  }
  
  /* Texts */
  h1 {
    color: #EEE !important;
  }
  .intro-text {
    color: #388e3c !important;
  }
  
  /*************/
  /* Chat page */
  /*************/
  
  /* Background */
  .pane-body, .pane-chat-tile, .msg-unread {
    background: #444242 !important;
  }
  
  /* Recived bubble menu arrow on hover */
  jZ4tp._1i1U7.se9J6, ._1i1U7.jZ4tp {
    background: linear-gradient(to left, rgba(255, 255, 255, 0) 0%, #303030 50%) !important;
  }
  /* Sent bubble menu arrow on hover */
  ._2DNgV._1i1U7 {
    background: linear-gradient(to left, rgba(255, 255, 255, 0) 0%, #264441 50%) !important;
  }
  
  /* Chat bubble */
  .message-in {
    background: #303030 !important;  
  }
  .message-out {
    background: #264441 !important;
  }
  .tail {
   margin-top: 1.5px !important; 
  }
  .message-system-body, .message-system {
    background: #388e3c !important;
    color: #EEE !important;
    font-weight: bold !important;
    text-shadow: none !important;
  }
  .msg-unread-body {
    font-weight: bold !important;
  }
  
  /* Remove background from down arrow */
  .message .btn-context, .message .btn-context:after, .context-in.context, .context-out.context  {
    background: none !important;
  }
  
  /* Buttom bar */
  .pane-footer, .block-compose, .compose-popup-panel, .btn-background {
    background: #616161!important;
  }
  .input-container {
    background: #EEE !important;
  }
  
  /* New message down arrow */
  .incoming-msgs {
    background: #264441 !important;
    right: 35px;
  }
  
  /* Emojis */
  button.menu-item, .emoji-panel-header {
    background: #212121 !important;
    transition-duration: .25s !important;
  }
  .menu-tabs-marker {
    background: #4caf50 !important;
  }
  .emoji-panel-body, .gif-search-container, .nib {
    background: #616161 !important;
  }
  .input-gif-search, .input-emoji-search  {
    background: #EEE !important;
  }
  .emojik {
    border-radius: 4px !important;
    -webkit-box-shadow: 2px 2px 6px 0px rgba(0,0,0,0.7);
    box-shadow: 2px 2px 6px 0px rgba(0,0,0,0.7);
    margin: 6px 4px !important;
  }
  
  /**********/
  /* Others */
  /**********/
  
  /* Headers */
  .pane-list-header, .unread-count, button.btn-plain {
    background: #388e3c !important; 
  }
  .pane-chat-header {
    background: #455a64 !important;
  }
  header {
    background: #00796b !important;
  }
  
  
  /* Dropdowns */
  .dropdown {
   background: #444242!important;   
  }
  
  li .ellipsify {
    color: #EEE !important;
  }
  li .ellipsify:hover {
    background: #444242 !important;
  }
  
  /* Popups */
  .popup {
    background: #444242 !important;
  }
  .popup-title, .btn-plain {
    color: #EEE !important;
  }
  .popup-contents {
    color: #BBB !important;
  }
  .select * {
    color: #00BFA5 !important;
  }
  .popup-controls-item {
    background: #4caf50 !important;
  }
  .backdrop {
    background: rgba(15, 15, 15, 0.65) !important;
  }
  
  /*  Image viewer */
  .media-panel-header, .media-content {
    background: #333030!important;
  }
  .chat-main {
    color: #388e3c !important;
  }
  .media-panel-header > .chat {
    background: #333030 !important;
  }
  
  .drawer-body {
    background: #363030 !important;
  }
  
  /* NO BORDERS! */
  * {
    border: none !important;   
  }
}
